-- ----------------------------------------------------------------------------
-- MODULE 1: Growth & Expansion Strategies
-- ----------------------------------------------------------------------------
INSERT INTO public.curriculum_modules (
    section_type,
    competency_name,
    competency_name_ar,
    order_index,
    icon,
    content,
    content_ar,
    description,
    description_ar,
    learning_objectives,
    learning_objectives_ar,
    estimated_duration_hours,
    prerequisite_module_id,
    quiz_id,
    quiz_required,
    quiz_passing_score,
    certification_type,
    is_published,
    created_at
) VALUES (
    'knowledge_based',
    'Growth & Expansion Strategies',
    'استراتيجيات النمو والتوسع',
    1,
    'trending-up',
    '{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"Growth & Expansion Strategies"}]},{"type":"paragraph","content":[{"type":"text","text":"In today''s rapidly evolving business landscape, sustainable growth is not merely an ambition—it is a strategic imperative. This foundational module introduces you to the core principles, frameworks, and methodologies that drive organizational expansion, market penetration, and long-term value creation in business development."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Understanding Growth Dynamics"}]},{"type":"paragraph","content":[{"type":"text","text":"Growth strategies encompass a comprehensive spectrum of approaches, ranging from organic expansion through product innovation and market development to inorganic growth via mergers, acquisitions, and strategic partnerships. As a business development professional, you must not only understand the theoretical underpinnings of each approach but also develop the practical judgment to align them with your organization''s strategic objectives, resource capabilities, and risk tolerance."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"The Ansoff Matrix: A Strategic Framework"}]},{"type":"paragraph","content":[{"type":"text","text":"The Ansoff Growth Matrix, developed by Igor Ansoff in 1957, provides a strategic lens for evaluating growth opportunities across four distinct dimensions:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Market Penetration"},{"type":"text","text":" - Increasing market share in existing markets with existing products through competitive pricing, enhanced marketing, or improved distribution. This low-risk strategy focuses on capturing more of the current market demand."}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Market Development"},{"type":"text","text":" - Entering new geographic markets or customer segments with existing products. This requires understanding new customer needs, regulatory environments, and competitive dynamics."}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Product Development"},{"type":"text","text":" - Creating new products or services for existing markets by leveraging customer relationships and market knowledge. Innovation and R&D capabilities are critical success factors."}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Diversification"},{"type":"text","text":" - Launching new products in new markets, representing the highest risk but also the greatest potential for transformational growth. Success requires substantial investment and careful risk management."}]}]}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Organic vs. Inorganic Growth Strategies"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Organic Growth"},{"type":"text","text":" relies on internal capabilities and resources—leveraging existing infrastructure, R&D investments, brand equity, and incremental market expansion. This approach offers several advantages:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Greater operational control and strategic alignment"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Preservation of organizational culture and values"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Lower financial risk and capital requirements"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Sustainable competitive advantages through internal capabilities"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"However, organic growth typically yields slower returns and may not provide sufficient velocity in rapidly evolving markets."}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Inorganic Growth"},{"type":"text","text":" accelerates expansion through external mechanisms—mergers, acquisitions, joint ventures, or strategic alliances. While faster, this path introduces significant challenges:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Complex integration of systems, processes, and cultures"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Higher upfront capital requirements and financial risk"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Potential cultural misalignment and employee resistance"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Regulatory scrutiny and compliance complexities"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Strategic Expansion Models"}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Geographic Expansion"}]},{"type":"paragraph","content":[{"type":"text","text":"Entering new geographic markets represents both significant opportunity and substantial risk. Successful geographic expansion requires meticulous assessment across multiple dimensions:"}]},{"type":"orderedList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Regulatory and Compliance Frameworks"},{"type":"text","text":" - Understanding local laws, licensing requirements, tax structures, and industry-specific regulations"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Cultural and Consumer Behavior"},{"type":"text","text":" - Analyzing purchasing patterns, brand preferences, communication styles, and decision-making processes"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Competitive Landscape"},{"type":"text","text":" - Evaluating local competitors, market concentration, pricing dynamics, and barriers to entry"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Infrastructure and Distribution"},{"type":"text","text":" - Assessing logistics capabilities, supply chain maturity, and channel partner availability"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Risk Factors"},{"type":"text","text":" - Identifying political stability, economic volatility, currency fluctuation, and social trends"}]}]}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Horizontal Integration"}]},{"type":"paragraph","content":[{"type":"text","text":"Horizontal integration involves acquiring or merging with competitors operating at the same stage of the value chain. This strategy enables economies of scale, market consolidation, enhanced pricing power, and geographic expansion. Key strategic considerations include:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Antitrust regulations and regulatory approval processes"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Brand architecture and portfolio rationalization"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Operational synergies and cost reduction opportunities"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Revenue synergies through cross-selling and market access"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Cultural integration and talent retention strategies"}]}]}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Vertical Integration"}]},{"type":"paragraph","content":[{"type":"text","text":"Vertical integration extends organizational control across the supply chain—either backward toward suppliers (backward integration) or forward toward distribution and end customers (forward integration). This approach delivers several strategic benefits:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Enhanced control over quality, costs, and delivery timelines"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Reduced dependency on external suppliers or distributors"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Improved margins through elimination of intermediary costs"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Greater market intelligence and customer insights"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"However, vertical integration requires substantial capital investment, operational expertise in new value chain stages, and increased organizational complexity."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Measuring Growth Success"}]},{"type":"paragraph","content":[{"type":"text","text":"Effective growth strategies must be measured against clear, quantifiable Key Performance Indicators (KPIs):"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Revenue Growth Rate and CAGR"},{"type":"text","text":" - Year-over-year revenue increase and Compound Annual Growth Rate over multi-year periods"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Market Share Gains"},{"type":"text","text":" - Percentage of total addressable market captured relative to competitors"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Customer Acquisition Cost (CAC) and Lifetime Value (LTV)"},{"type":"text","text":" - Cost to acquire new customers vs. total value generated over customer lifecycle"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Return on Investment (ROI) and Payback Period"},{"type":"text","text":" - Financial returns relative to investment and time to recover initial costs"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Operational Efficiency and Margin Improvement"},{"type":"text","text":" - EBITDA margins, operating leverage, and productivity metrics"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Practical Application and Case Studies"}]},{"type":"paragraph","content":[{"type":"text","text":"To illustrate these concepts, consider how leading organizations have successfully executed growth strategies:"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Case Study: Amazon''s Multi-Horizon Growth"},{"type":"text","text":" - Amazon exemplifies masterful execution across all growth dimensions: market penetration in e-commerce, geographic expansion into emerging markets, product development (AWS, Prime Video), and diversification into physical retail (Whole Foods). Their vertical integration strategy—from fulfillment centers to last-mile delivery—demonstrates the power of strategic control across the value chain."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Key Takeaways"}]},{"type":"paragraph","content":[{"type":"text","text":"As you complete this module, you will be equipped to:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Evaluate growth opportunities using proven strategic frameworks"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Design comprehensive expansion roadmaps aligned with organizational goals"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Assess risks systematically and develop robust mitigation strategies"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Present data-driven growth recommendations to executive stakeholders"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Navigate the complexities of organic vs. inorganic growth decisions"}]}]}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"italic"}],"text":"This module lays the essential foundation for all subsequent competencies in the BDA BoCK™ framework. Mastery of growth strategy principles is fundamental to effective business development practice at every organizational level."}]}]}',
    '{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"استراتيجيات النمو والتوسع"}]},{"type":"paragraph","content":[{"type":"text","text":"في مشهد الأعمال سريع التطور اليوم، النمو المستدام ليس مجرد طموح - بل هو ضرورة استراتيجية. تقدم لك هذه الوحدة التأسيسية المبادئ والأطر والمنهجيات الأساسية التي تدفع التوسع التنظيمي واختراق السوق وخلق القيمة طويلة الأجل في تطوير الأعمال."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"فهم ديناميكيات النمو"}]},{"type":"paragraph","content":[{"type":"text","text":"تشمل استراتيجيات النمو طيفًا شاملاً من الأساليب، تتراوح من التوسع العضوي من خلال الابتكار في المنتجات وتطوير السوق إلى النمو غير العضوي عبر عمليات الدمج والاستحواذ والشراكات الاستراتيجية."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"مصفوفة أنسوف: إطار استراتيجي"}]},{"type":"paragraph","content":[{"type":"text","text":"توفر مصفوفة النمو لأنسوف عدسة استراتيجية لتقييم فرص النمو عبر أربعة أبعاد متميزة:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"اختراق السوق"},{"type":"text","text":" - زيادة حصة السوق في الأسواق الحالية بالمنتجات الحالية"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"تطوير السوق"},{"type":"text","text":" - دخول أسواق جغرافية جديدة أو شرائح عملاء جديدة بالمنتجات الحالية"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"تطوير المنتج"},{"type":"text","text":" - إنشاء منتجات أو خدمات جديدة للأسواق الحالية"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"التنويع"},{"type":"text","text":" - إطلاق منتجات جديدة في أسواق جديدة"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"النقاط الرئيسية"}]},{"type":"paragraph","content":[{"type":"text","text":"عند إكمال هذه الوحدة، ستكون مجهزًا لتقييم فرص النمو باستخدام الأطر الاستراتيجية المثبتة، وتصميم خرائط طريق شاملة للتوسع متوافقة مع الأهداف التنظيمية، وتقييم المخاطر بشكل منهجي وتطوير استراتيجيات تخفيف قوية."}]}]}',
    'Master the fundamentals of strategic growth planning, market expansion models, and sustainable value creation frameworks essential for business development success.',
    'إتقان أساسيات التخطيط الاستراتيجي للنمو ونماذج التوسع في الأسواق وأطر خلق القيمة المستدامة الضرورية لنجاح تطوير الأعمال.',
    ARRAY[
        'Understand and apply the Ansoff Matrix to evaluate growth opportunities across four strategic dimensions',
        'Distinguish between organic and inorganic growth strategies and assess their relative advantages',
        'Design comprehensive geographic expansion plans considering regulatory, cultural, and competitive factors',
        'Evaluate horizontal and vertical integration opportunities with clear risk-return analysis',
        'Establish KPIs and measurement frameworks to track growth initiative performance'
    ],
    ARRAY[
        'فهم وتطبيق مصفوفة أنسوف لتقييم فرص النمو عبر أربعة أبعاد استراتيجية',
        'التمييز بين استراتيجيات النمو العضوي وغير العضوي وتقييم مزاياها النسبية',
        'تصميم خطط توسع جغرافي شاملة تأخذ في الاعتبار العوامل التنظيمية والثقافية والتنافسية',
        'تقييم فرص التكامل الأفقي والعمودي مع تحليل واضح للمخاطر والعوائد',
        'إنشاء مؤشرات الأداء الرئيسية وأطر القياس لتتبع أداء مبادرات النمو'
    ],
    3,
    NULL, -- No prerequisite for first module
    NULL, -- Quiz to be linked later
    true,
    70,
    'CP',
    true,
    NOW()
);

-- Get Module 1 ID for prerequisite chain
DO $$
DECLARE
    v_module_1_id UUID;
    v_module_2_id UUID;
    v_module_3_id UUID;
    v_module_4_id UUID;
    v_module_5_id UUID;
    v_module_6_id UUID;
    v_module_7_id UUID;
    v_module_8_id UUID;
    v_module_9_id UUID;
    v_module_10_id UUID;
    v_module_11_id UUID;
    v_module_12_id UUID;
    v_module_13_id UUID;
BEGIN
    -- Get Module 1 ID
    SELECT id INTO v_module_1_id
    FROM public.curriculum_modules
    WHERE order_index = 1 AND certification_type = 'CP';

    -- ----------------------------------------------------------------------------
    -- MODULE 2: Market & Competitive Analysis
    -- ----------------------------------------------------------------------------
    INSERT INTO public.curriculum_modules (
        section_type, competency_name, competency_name_ar, order_index, icon,
        content, content_ar, description, description_ar,
        learning_objectives, learning_objectives_ar,
        estimated_duration_hours, prerequisite_module_id, quiz_id,
        quiz_required, quiz_passing_score, certification_type, is_published, created_at
    ) VALUES (
        'knowledge_based',
        'Market & Competitive Analysis',
        'تحليل السوق والمنافسة',
        2,
        'search',
        '{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"Market & Competitive Analysis"}]},{"type":"paragraph","content":[{"type":"text","text":"In an era characterized by rapid market evolution, technological disruption, and intensifying global competition, the ability to systematically analyze markets and competitive dynamics has become a mission-critical business development competency. This module equips you with advanced analytical frameworks, research methodologies, and strategic thinking tools to make informed, data-driven decisions that drive sustainable competitive advantage."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Market Analysis Fundamentals"}]},{"type":"paragraph","content":[{"type":"text","text":"Effective market analysis transcends simple size estimation—it requires a sophisticated, multi-layered approach that examines market structure, growth trajectories, customer segmentation, buying behaviors, and ecosystem dynamics. Understanding these interconnected dimensions enables business development professionals to identify whitespace opportunities, assess market viability, and develop targeted go-to-market strategies."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"TAM, SAM, and SOM Framework"}]},{"type":"paragraph","content":[{"type":"text","text":"The TAM-SAM-SOM framework provides a structured approach to market opportunity quantification:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Total Addressable Market (TAM)"},{"type":"text","text":" - The complete revenue opportunity available if your organization achieved 100% market share with zero constraints. TAM is calculated using top-down (market research data) or bottom-up (unit economics) methodologies."}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Serviceable Available Market (SAM)"},{"type":"text","text":" - The portion of TAM that your business model, geographic reach, regulatory permissions, and resource capabilities can realistically serve. SAM accounts for practical constraints and strategic focus areas."}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Serviceable Obtainable Market (SOM)"},{"type":"text","text":" - The realistic market share you can capture in the short-to-medium term (typically 3-5 years) given competitive dynamics, sales capacity, and market penetration rates. SOM drives revenue forecasting and resource planning."}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Competitive Intelligence"}]},{"type":"paragraph","content":[{"type":"text","text":"Competitive analysis extends far beyond identifying who your competitors are—it involves developing deep, nuanced understanding of their strategies, capabilities, market positioning, financial health, innovation pipelines, and strategic vulnerabilities. Effective competitive intelligence creates information asymmetry that informs superior strategic decision-making."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Porter''s Five Forces Framework"}]},{"type":"paragraph","content":[{"type":"text","text":"Michael Porter''s seminal framework remains foundational for assessing industry structure and competitive intensity. The five forces that shape industry profitability are:"}]},{"type":"orderedList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Threat of New Entrants"},{"type":"text","text":" - Barriers to entry including capital requirements, economies of scale, brand loyalty, regulatory approvals, access to distribution channels, and proprietary technology. Lower barriers increase competitive pressure and erode profitability."}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Bargaining Power of Suppliers"},{"type":"text","text":" - Supplier concentration relative to buyer concentration, uniqueness of supplied products or services, switching costs, and threat of forward integration. High supplier power constrains margins and operational flexibility."}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Bargaining Power of Buyers"},{"type":"text","text":" - Customer concentration, price sensitivity, product differentiation, switching costs, and threat of backward integration. Sophisticated buyers with multiple options can extract value through aggressive negotiations."}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Threat of Substitute Products"},{"type":"text","text":" - Alternative solutions that fulfill the same customer need through different approaches, technologies, or business models. Disruptive innovations often emerge as substitutes that render existing solutions obsolete."}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Rivalry Among Existing Competitors"},{"type":"text","text":" - Industry concentration, growth rate, product differentiation, fixed costs, exit barriers, and strategic diversity. Intense rivalry manifests through price wars, promotional battles, and innovation races that erode profitability."}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Strategic Positioning"}]},{"type":"paragraph","content":[{"type":"text","text":"Strategic positioning defines where your organization chooses to compete and how it differentiates itself from competitors. Effective positioning creates defensible competitive advantages that are difficult for rivals to replicate or neutralize."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Perceptual Mapping and Competitive Differentiation"}]},{"type":"paragraph","content":[{"type":"text","text":"Perceptual maps visualize how customers perceive competitors across two critical dimensions (e.g., price vs. quality, innovation vs. reliability). These maps reveal market gaps, crowded competitive spaces, and differentiation opportunities. Combine perceptual mapping with SWOT analysis and competitive benchmarking to identify your unique value proposition and sustainable competitive advantages."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Blue Ocean Strategy"}]},{"type":"paragraph","content":[{"type":"text","text":"Rather than competing in crowded red oceans characterized by intense competition and commoditization, Blue Ocean Strategy advocates creating uncontested market space through value innovation—simultaneously pursuing differentiation and cost leadership. This requires challenging industry assumptions, eliminating factors that add cost without value, reducing factors below industry standards, raising factors above industry standards, and creating new factors the industry has never offered."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Data-Driven Market Intelligence"}]},{"type":"paragraph","content":[{"type":"text","text":"Modern market analysis leverages diverse data sources and analytical techniques:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Primary Research"},{"type":"text","text":" - Surveys, in-depth interviews, focus groups, ethnographic studies, and customer advisory boards that generate original insights directly from target markets"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Secondary Research"},{"type":"text","text":" - Industry reports, market research databases (Gartner, Forrester), public company filings, trade association data, and academic research"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Digital Intelligence"},{"type":"text","text":" - Web analytics, social media listening, search engine optimization data, online review analysis, and digital footprint tracking"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Competitive Monitoring"},{"type":"text","text":" - Patent filings, job postings, technology stack analysis, pricing changes, partnership announcements, and product roadmap indicators"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Practical Application"}]},{"type":"paragraph","content":[{"type":"text","text":"Mastery of market and competitive analysis enables you to answer critical strategic questions: Which markets should we enter? What customer segments should we target? How should we position our offerings? What pricing strategy maximizes value capture? Which competitors pose the greatest threats? Where are the opportunities for differentiation?"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"italic"}],"text":"These analytical capabilities form the foundation for evidence-based strategy development and are essential for every business development professional seeking to drive sustainable competitive advantage."}]}]}',
        '{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"تحليل السوق والمنافسة"}]},{"type":"paragraph","content":[{"type":"text","text":"في عصر يتميز بالتطور السريع للسوق والاضطراب التكنولوجي وتكثيف المنافسة العالمية، أصبحت القدرة على تحليل الأسواق والديناميكيات التنافسية بشكل منهجي كفاءة بالغة الأهمية لتطوير الأعمال."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"أساسيات تحليل السوق"}]},{"type":"paragraph","content":[{"type":"text","text":"يتجاوز التحليل الفعال للسوق مجرد تقدير الحجم - فهو يتطلب نهجًا متطورًا متعدد الطبقات يفحص هيكل السوق ومسارات النمو وتجزئة العملاء وسلوكيات الشراء وديناميكيات النظام البيئي."}]}]}',
        'Develop advanced skills in market sizing, competitive intelligence, strategic positioning, and data-driven analysis to inform business development decisions.',
        'تطوير مهارات متقدمة في تحديد حجم السوق والذكاء التنافسي والتموضع الاستراتيجي والتحليل القائم على البيانات لإبلاغ قرارات تطوير الأعمال.',
        ARRAY[
            'Calculate and interpret TAM, SAM, and SOM for comprehensive market opportunity assessment',
            'Apply Porter''s Five Forces to evaluate industry attractiveness and competitive dynamics',
            'Conduct rigorous competitive analysis, benchmarking, and intelligence gathering',
            'Identify strategic positioning opportunities, whitespace, and differentiation strategies',
            'Leverage diverse data sources and analytical techniques for market intelligence'
        ],
        ARRAY[
            'حساب وتفسير TAM و SAM و SOM لتقييم شامل لفرص السوق',
            'تطبيق قوى بورتر الخمس لتقييم جاذبية الصناعة والديناميكيات التنافسية',
            'إجراء تحليل تنافسي صارم ومقارنة معيارية وجمع معلومات',
            'تحديد فرص التموضع الاستراتيجي والمساحات البيضاء واستراتيجيات التمايز',
            'الاستفادة من مصادر البيانات المتنوعة والتقنيات التحليلية لذكاء السوق'
        ],
        4,
        v_module_1_id,
        NULL,
        true,
        70,
        'CP',
        true,
        NOW()
    );

    -- Get Module 2 ID
    SELECT id INTO v_module_2_id
    FROM public.curriculum_modules
    WHERE order_index = 2 AND certification_type = 'CP';

    -- ----------------------------------------------------------------------------
    -- MODULE 3: Innovation in Business Development
    -- ----------------------------------------------------------------------------
    INSERT INTO public.curriculum_modules (
        section_type, competency_name, competency_name_ar, order_index, icon,
        content, content_ar, description, description_ar,
        learning_objectives, learning_objectives_ar,
        estimated_duration_hours, prerequisite_module_id, quiz_id,
        quiz_required, quiz_passing_score, certification_type, is_published, created_at
    ) VALUES (
        'knowledge_based',
        'Innovation in Business Development',
        'الابتكار في تطوير الأعمال',
        3,
        'lightbulb',
        '{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"Innovation in Business Development"}]},{"type":"paragraph","content":[{"type":"text","text":"Innovation is the lifeblood of sustainable competitive advantage in modern business development. This module explores systematic approaches to fostering innovation, from disruptive technologies to business model transformation, equipping you with frameworks and methodologies to drive breakthrough value creation."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Understanding Innovation Types"}]},{"type":"paragraph","content":[{"type":"text","text":"Innovation manifests across multiple dimensions, each requiring distinct strategic approaches and organizational capabilities:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Product Innovation"},{"type":"text","text":" - New features, capabilities, or entirely new product categories that deliver superior customer value"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Process Innovation"},{"type":"text","text":" - Operational improvements that increase efficiency, reduce costs, or enhance quality"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Business Model Innovation"},{"type":"text","text":" - Fundamental changes in how value is created, delivered, and captured"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Service Innovation"},{"type":"text","text":" - Novel service offerings or delivery mechanisms that differentiate customer experience"}]}]}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"The Innovation Spectrum"}]},{"type":"paragraph","content":[{"type":"text","text":"Clayton Christensen''s framework distinguishes between sustaining and disruptive innovations. Sustaining innovations improve existing products along dimensions valued by mainstream customers. Disruptive innovations initially underperform on traditional metrics but offer new value propositions—typically simpler, more convenient, or more affordable—that appeal to overlooked customer segments before eventually displacing established solutions."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Innovation Frameworks and Methodologies"}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Design Thinking"}]},{"type":"paragraph","content":[{"type":"text","text":"Design Thinking is a human-centered innovation approach emphasizing empathy, experimentation, and iterative refinement. The five-stage process includes:"}]},{"type":"orderedList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Empathize"},{"type":"text","text":" - Deep immersion in customer contexts to understand latent needs and pain points"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Define"},{"type":"text","text":" - Synthesizing insights to articulate the core problem statement"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Ideate"},{"type":"text","text":" - Generating diverse solution concepts through brainstorming and creative techniques"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Prototype"},{"type":"text","text":" - Creating rapid, low-fidelity representations to explore solution feasibility"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Test"},{"type":"text","text":" - Gathering feedback, validating assumptions, and refining solutions"}]}]}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Lean Startup Methodology"}]},{"type":"paragraph","content":[{"type":"text","text":"Eric Ries'' Lean Startup approach minimizes resource waste through rapid experimentation. The Build-Measure-Learn loop accelerates validated learning:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Build"},{"type":"text","text":" a Minimum Viable Product (MVP) - the simplest version capable of testing hypotheses"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Measure"},{"type":"text","text":" actionable metrics that reflect true progress versus vanity metrics"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Learn"},{"type":"text","text":" whether to persevere with the current strategy or pivot to alternative approaches"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Business Model Innovation"}]},{"type":"paragraph","content":[{"type":"text","text":"The Business Model Canvas, developed by Alexander Osterwalder, provides a strategic tool for designing, analyzing, and innovating business models across nine building blocks:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Customer Segments - distinct groups with different needs and characteristics"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Value Propositions - bundles of products and services creating value for segments"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Channels - how value propositions reach customers"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Customer Relationships - types of relationships established with segments"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Revenue Streams - how the business generates income from each segment"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Key Resources - assets required to make the business model work"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Key Activities - critical actions for successful operation"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Key Partnerships - network of suppliers and partners"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Cost Structure - all costs inherent in business model operation"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Technology-Driven Innovation"}]},{"type":"paragraph","content":[{"type":"text","text":"Emerging technologies create unprecedented opportunities for business development innovation:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Artificial Intelligence & Machine Learning"},{"type":"text","text":" - Predictive analytics, personalization, automation, and decision support"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Internet of Things (IoT)"},{"type":"text","text":" - Connected devices enabling new data streams and service models"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Blockchain"},{"type":"text","text":" - Decentralized trust, transparency, and new transaction mechanisms"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Cloud Computing"},{"type":"text","text":" - Scalable infrastructure reducing capital intensity and enabling rapid experimentation"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Cultivating an Innovation Culture"}]},{"type":"paragraph","content":[{"type":"text","text":"Sustainable innovation requires organizational cultures that embrace experimentation, accept intelligent failure, and reward creative risk-taking. Key cultural attributes include psychological safety, dedicated resources for exploration, cross-functional collaboration, customer-centricity, and leadership commitment to long-term innovation investments."}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"italic"}],"text":"Mastering innovation frameworks positions business development professionals to identify and capture emerging opportunities before competitors, driving sustained organizational growth and market leadership."}]}]}',
        '{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"الابتكار في تطوير الأعمال"}]},{"type":"paragraph","content":[{"type":"text","text":"الابتكار هو شريان الحياة للميزة التنافسية المستدامة في تطوير الأعمال الحديثة. تستكشف هذه الوحدة الأساليب المنهجية لتعزيز الابتكار، من التقنيات الثورية إلى تحول نماذج الأعمال."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"فهم أنواع الابتكار"}]},{"type":"paragraph","content":[{"type":"text","text":"يتجلى الابتكار عبر أبعاد متعددة، يتطلب كل منها أساليب استراتيجية وقدرات تنظيمية متميزة."}]}]}',
        'Master systematic innovation approaches, from disruptive technologies to business model transformation, enabling breakthrough value creation and competitive advantage.',
        'إتقان أساليب الابتكار المنهجية، من التقنيات الثورية إلى تحول نماذج الأعمال، لتمكين خلق قيمة اختراقية وميزة تنافسية.',
        ARRAY[
            'Distinguish between sustaining and disruptive innovation strategies',
            'Apply Design Thinking methodology to identify and solve customer problems',
            'Implement Lean Startup principles for rapid experimentation and validated learning',
            'Utilize Business Model Canvas for strategic innovation and transformation',
            'Evaluate emerging technologies for business development opportunities'
        ],
        ARRAY[
            'التمييز بين استراتيجيات الابتكار المستدامة والمدمرة',
            'تطبيق منهجية التفكير التصميمي لتحديد وحل مشاكل العملاء',
            'تنفيذ مبادئ الشركات الناشئة الرشيقة للتجريب السريع والتعلم المعتمد',
            'استخدام لوحة نموذج الأعمال للابتكار والتحول الاستراتيجي',
            'تقييم التقنيات الناشئة لفرص تطوير الأعمال'
        ],
        3,
        v_module_2_id,
        NULL,
        true,
        70,
        'CP',
        true,
        NOW()
    );

    -- Get Module 3 ID
    SELECT id INTO v_module_3_id
    FROM public.curriculum_modules
    WHERE order_index = 3 AND certification_type = 'CP';

    -- ----------------------------------------------------------------------------
    -- MODULE 4: Business Project Management
    -- ----------------------------------------------------------------------------
    INSERT INTO public.curriculum_modules (
        section_type, competency_name, competency_name_ar, order_index, icon,
        content, content_ar, description, description_ar,
        learning_objectives, learning_objectives_ar,
        estimated_duration_hours, prerequisite_module_id, quiz_id,
        quiz_required, quiz_passing_score, certification_type, is_published, created_at
    ) VALUES (
        'knowledge_based',
        'Business Project Management',
        'إدارة مشاريع الأعمال',
        4,
        'clipboard-check',
        '{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"Business Project Management"}]},{"type":"paragraph","content":[{"type":"text","text":"Effective project management is fundamental to business development success. This module provides comprehensive frameworks and practical tools for planning, executing, and delivering complex business initiatives on time, within budget, and aligned with strategic objectives."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Project Management Fundamentals"}]},{"type":"paragraph","content":[{"type":"text","text":"Projects are temporary endeavors undertaken to create unique products, services, or results. Unlike ongoing operations, projects have defined beginnings and endings, specific objectives, and resource constraints. Business development projects—market entry initiatives, partnership formations, new product launches—require disciplined management to navigate complexity and uncertainty."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"The Project Life Cycle"}]},{"type":"paragraph","content":[{"type":"text","text":"Projects progress through distinct phases:"}]},{"type":"orderedList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Initiation"},{"type":"text","text":" - Defining project purpose, objectives, and stakeholders; developing business case; securing authorization"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Planning"},{"type":"text","text":" - Establishing scope, schedule, budget, quality standards, risk strategies, and communication protocols"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Execution"},{"type":"text","text":" - Coordinating resources, managing teams, implementing plans, and delivering work packages"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Monitoring & Control"},{"type":"text","text":" - Tracking performance, identifying variances, implementing corrective actions"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Closure"},{"type":"text","text":" - Finalizing deliverables, documenting lessons learned, releasing resources"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Project Management Methodologies"}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Waterfall Approach"}]},{"type":"paragraph","content":[{"type":"text","text":"Traditional waterfall methodology follows sequential phases with formal stage gates. Requirements are defined comprehensively upfront, with minimal changes during execution. This approach suits projects with stable requirements, well-understood technologies, and regulatory compliance needs. However, it offers limited flexibility for evolving requirements or emerging opportunities."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Agile Methodologies"}]},{"type":"paragraph","content":[{"type":"text","text":"Agile approaches embrace iterative development, continuous feedback, and adaptive planning. The Agile Manifesto prioritizes individuals and interactions over processes and tools, working solutions over comprehensive documentation, customer collaboration over contract negotiation, and responding to change over following a plan."}]},{"type":"paragraph","content":[{"type":"text","text":"Popular Agile frameworks include:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Scrum"},{"type":"text","text":" - Time-boxed sprints (typically 2-4 weeks) with defined roles (Product Owner, Scrum Master, Development Team) and ceremonies (sprint planning, daily standups, reviews, retrospectives)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Kanban"},{"type":"text","text":" - Visualizing workflow, limiting work-in-progress, and optimizing flow through continuous delivery"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"SAFe"},{"type":"text","text":" - Scaled Agile Framework for coordinating multiple teams on large enterprise initiatives"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Critical Project Management Tools"}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Work Breakdown Structure (WBS)"}]},{"type":"paragraph","content":[{"type":"text","text":"The WBS decomposes project scope into manageable work packages, creating a hierarchical representation of all deliverables. Each level provides increasing detail, enabling accurate estimation, resource allocation, and progress tracking."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Gantt Charts and Critical Path Method"}]},{"type":"paragraph","content":[{"type":"text","text":"Gantt charts visualize project schedules, showing task dependencies, durations, and resource assignments. Critical Path Method (CPM) identifies the longest sequence of dependent activities determining minimum project duration. Tasks on the critical path have zero slack—any delay directly impacts project completion."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Risk Management Framework"}]},{"type":"paragraph","content":[{"type":"text","text":"Systematic risk management involves:"}]},{"type":"orderedList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Risk Identification"},{"type":"text","text":" - Brainstorming potential threats and opportunities affecting objectives"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Risk Analysis"},{"type":"text","text":" - Assessing probability and impact to prioritize attention"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Risk Response Planning"},{"type":"text","text":" - Developing strategies to avoid, transfer, mitigate, or accept risks"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Risk Monitoring"},{"type":"text","text":" - Tracking identified risks and identifying new risks throughout project lifecycle"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Stakeholder Management"}]},{"type":"paragraph","content":[{"type":"text","text":"Projects exist within complex stakeholder ecosystems. Effective stakeholder management requires identifying all parties affecting or affected by the project, analyzing their interests and influence, and developing targeted engagement strategies. The power-interest matrix helps prioritize stakeholders: manage closely those with high power and high interest, keep satisfied those with high power but lower interest, keep informed those with high interest but lower power, and monitor those with low power and low interest."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Performance Measurement"}]},{"type":"paragraph","content":[{"type":"text","text":"Earned Value Management (EVM) integrates scope, schedule, and cost to assess project performance. Key metrics include:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Planned Value (PV)"},{"type":"text","text":" - Budgeted cost of work scheduled"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Earned Value (EV)"},{"type":"text","text":" - Budgeted cost of work actually performed"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Actual Cost (AC)"},{"type":"text","text":" - Actual cost incurred for work performed"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Schedule Variance (SV)"},{"type":"text","text":" - EV - PV (positive = ahead of schedule)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Cost Variance (CV)"},{"type":"text","text":" - EV - AC (positive = under budget)"}]}]}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"italic"}],"text":"Mastering project management disciplines enables business development professionals to translate strategic vision into operational reality, delivering measurable business impact consistently."}]}]}',
        '{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"إدارة مشاريع الأعمال"}]},{"type":"paragraph","content":[{"type":"text","text":"الإدارة الفعالة للمشاريع أساسية لنجاح تطوير الأعمال. توفر هذه الوحدة أطرًا شاملة وأدوات عملية للتخطيط والتنفيذ وتسليم مبادرات الأعمال المعقدة."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"أساسيات إدارة المشاريع"}]},{"type":"paragraph","content":[{"type":"text","text":"المشاريع هي مساعي مؤقتة يتم القيام بها لإنشاء منتجات أو خدمات أو نتائج فريدة."}]}]}',
        'Master comprehensive project management frameworks and tools for planning, executing, and delivering complex business development initiatives successfully.',
        'إتقان أطر إدارة المشاريع الشاملة والأدوات للتخطيط والتنفيذ وتسليم مبادرات تطوير الأعمال المعقدة بنجاح.',
        ARRAY[
            'Apply project management life cycle across business development initiatives',
            'Select appropriate methodologies (Waterfall, Agile, Hybrid) for different project contexts',
            'Utilize WBS, Gantt charts, and critical path analysis for effective planning',
            'Implement systematic risk management throughout project execution',
            'Measure project performance using Earned Value Management techniques'
        ],
        ARRAY[
            'تطبيق دورة حياة إدارة المشاريع عبر مبادرات تطوير الأعمال',
            'اختيار المنهجيات المناسبة للسياقات المختلفة للمشروع',
            'استخدام هيكل تقسيم العمل ومخططات جانت وتحليل المسار الحرج',
            'تنفيذ إدارة المخاطر المنهجية طوال تنفيذ المشروع',
            'قياس أداء المشروع باستخدام تقنيات إدارة القيمة المكتسبة'
        ],
        3,
        v_module_3_id,
        NULL,
        true,
        70,
        'CP',
        true,
        NOW()
    );

    -- Get Module 4 ID
    SELECT id INTO v_module_4_id
    FROM public.curriculum_modules
    WHERE order_index = 4 AND certification_type = 'CP';

    -- ----------------------------------------------------------------------------
    -- MODULE 5: Financial & Pricing Models
    -- ----------------------------------------------------------------------------
    INSERT INTO public.curriculum_modules (
        section_type, competency_name, competency_name_ar, order_index, icon,
        content, content_ar, description, description_ar,
        learning_objectives, learning_objectives_ar,
        estimated_duration_hours, prerequisite_module_id, quiz_id,
        quiz_required, quiz_passing_score, certification_type, is_published, created_at
    ) VALUES (
        'knowledge_based',
        'Financial & Pricing Models',
        'النماذج المالية والتسعير',
        5,
        'dollar-sign',
        '{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"Financial & Pricing Models"}]},{"type":"paragraph","content":[{"type":"text","text":"Financial acumen and strategic pricing mastery are critical competencies for business development professionals. This module equips you with sophisticated analytical tools to evaluate investment opportunities, develop pricing strategies, and communicate financial value propositions to stakeholders."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Financial Analysis Fundamentals"}]},{"type":"paragraph","content":[{"type":"text","text":"Understanding financial statements—income statements, balance sheets, and cash flow statements—provides essential insights into organizational health, performance trends, and resource allocation decisions. Business development professionals must interpret these documents to assess partnership opportunities, evaluate acquisition targets, and build credible business cases."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Key Financial Metrics"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Profitability Ratios"},{"type":"text","text":" - Gross margin, operating margin, net margin, EBITDA revealing operational efficiency"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Liquidity Ratios"},{"type":"text","text":" - Current ratio, quick ratio assessing ability to meet short-term obligations"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Leverage Ratios"},{"type":"text","text":" - Debt-to-equity, interest coverage indicating financial risk and capital structure"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Efficiency Ratios"},{"type":"text","text":" - Asset turnover, inventory turnover, receivables days measuring operational effectiveness"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Valuation Multiples"},{"type":"text","text":" - P/E ratio, EV/EBITDA, Price-to-Sales for market-based valuation"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Investment Evaluation"}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Net Present Value (NPV)"}]},{"type":"paragraph","content":[{"type":"text","text":"NPV calculates the present value of future cash flows discounted by the cost of capital, minus initial investment. Positive NPV indicates value creation:"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"NPV = Σ [Cash Flow_t / (1 + r)^t] - Initial Investment"}]},{"type":"paragraph","content":[{"type":"text","text":"where r = discount rate and t = time period. NPV accounts for time value of money and enables comparison of projects with different timing and risk profiles."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Internal Rate of Return (IRR)"}]},{"type":"paragraph","content":[{"type":"text","text":"IRR represents the discount rate at which NPV equals zero—effectively the break-even return. Projects with IRR exceeding the cost of capital create value. IRR facilitates ranking investments and communicating expected returns to stakeholders."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Payback Period"}]},{"type":"paragraph","content":[{"type":"text","text":"Payback period measures time required to recover initial investment. While simple to calculate and communicate, it ignores time value of money and cash flows beyond payback, making it insufficient as a standalone metric."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Strategic Pricing Models"}]},{"type":"paragraph","content":[{"type":"text","text":"Pricing strategy directly impacts revenue, profitability, market positioning, and customer perception. Effective pricing requires understanding costs, value perception, competitive dynamics, and customer psychology."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Cost-Based Pricing"}]},{"type":"paragraph","content":[{"type":"text","text":"Cost-plus pricing adds a standard markup to unit costs. While ensuring cost recovery, this approach ignores customer willingness to pay and competitive positioning, potentially leaving value on the table or pricing out of market."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Value-Based Pricing"}]},{"type":"paragraph","content":[{"type":"text","text":"Value-based pricing aligns prices with customer-perceived value rather than costs. This requires deep understanding of customer economics, quantifying value drivers, and differentiating offerings. Value-based approaches maximize profitability when value delivered significantly exceeds costs."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Competitive Pricing"}]},{"type":"paragraph","content":[{"type":"text","text":"Competitive pricing considers market rates and competitor positioning. Strategies include:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Premium Pricing"},{"type":"text","text":" - Above-market pricing signaling superior quality or exclusivity"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Market Pricing"},{"type":"text","text":" - Matching prevailing market rates"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Penetration Pricing"},{"type":"text","text":" - Below-market pricing to gain market share rapidly"}]}]}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Dynamic and Personalized Pricing"}]},{"type":"paragraph","content":[{"type":"text","text":"Advanced pricing leverages data analytics and technology to optimize prices in real-time based on demand, inventory, customer segments, and contextual factors. Airlines, hotels, and e-commerce platforms exemplify dynamic pricing, balancing yield optimization with customer fairness perceptions."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Pricing Psychology"}]},{"type":"paragraph","content":[{"type":"text","text":"Understanding behavioral economics enhances pricing effectiveness:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Anchoring"},{"type":"text","text":" - Initial price points influence subsequent value judgments"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Price Ending"},{"type":"text","text":" - Prices ending in .99 perceived as significantly lower than round numbers"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Bundling"},{"type":"text","text":" - Package pricing can increase perceived value and average transaction size"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Reference Pricing"},{"type":"text","text":" - Displaying original prices alongside discounts amplifies savings perception"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Financial Modeling for Business Cases"}]},{"type":"paragraph","content":[{"type":"text","text":"Compelling business cases require robust financial models projecting revenues, costs, investments, and returns across multi-year horizons. Best practices include:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Clear assumptions documented and defensible"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Scenario analysis exploring best-case, base-case, and worst-case outcomes"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Sensitivity analysis identifying key value drivers and risk factors"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Monte Carlo simulation for probabilistic risk assessment"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Executive-friendly visualizations communicating complex analyses clearly"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Working Capital Management"}]},{"type":"paragraph","content":[{"type":"text","text":"Effective working capital management ensures operational liquidity while minimizing tied-up capital. Key levers include optimizing accounts receivable collection, managing inventory levels, and negotiating favorable payment terms with suppliers. The cash conversion cycle—days inventory outstanding plus days sales outstanding minus days payables outstanding—measures working capital efficiency."}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"italic"}],"text":"Financial literacy and pricing expertise enable business development professionals to quantify value propositions, optimize deal structures, and communicate business impact in the language of executive decision-makers."}]}]}',
        '{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"النماذج المالية والتسعير"}]},{"type":"paragraph","content":[{"type":"text","text":"الفطنة المالية وإتقان التسعير الاستراتيجي هي كفاءات حاسمة لمحترفي تطوير الأعمال. تزودك هذه الوحدة بأدوات تحليلية متطورة لتقييم فرص الاستثمار."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"أساسيات التحليل المالي"}]},{"type":"paragraph","content":[{"type":"text","text":"فهم البيانات المالية يوفر رؤى أساسية حول الصحة التنظيمية واتجاهات الأداء وقرارات تخصيص الموارد."}]}]}',
        'Develop financial analysis expertise and strategic pricing capabilities to evaluate investments, optimize deal structures, and communicate value propositions effectively.',
        'تطوير خبرة التحليل المالي وقدرات التسعير الاستراتيجي لتقييم الاستثمارات وتحسين هياكل الصفقات وإيصال عروض القيمة بفعالية.',
        ARRAY[
            'Analyze financial statements and calculate key performance ratios',
            'Apply NPV, IRR, and payback period for investment evaluation',
            'Design pricing strategies using cost-based, value-based, and competitive approaches',
            'Leverage pricing psychology and behavioral economics principles',
            'Build comprehensive financial models for business case development'
        ],
        ARRAY[
            'تحليل البيانات المالية وحساب نسب الأداء الرئيسية',
            'تطبيق صافي القيمة الحالية ومعدل العائد الداخلي لتقييم الاستثمار',
            'تصميم استراتيجيات التسعير باستخدام الأساليب القائمة على التكلفة والقيمة',
            'الاستفادة من علم نفس التسعير ومبادئ الاقتصاد السلوكي',
            'بناء نماذج مالية شاملة لتطوير حالة العمل'
        ],
        3,
        v_module_4_id,
        NULL,
        true,
        70,
        'CP',
        true,
        NOW()
    );

    -- Get Module 5 ID
    SELECT id INTO v_module_5_id
    FROM public.curriculum_modules
    WHERE order_index = 5 AND certification_type = 'CP';

    -- ----------------------------------------------------------------------------
    -- MODULE 6: Marketing & Sales Strategies
    -- ----------------------------------------------------------------------------
    INSERT INTO public.curriculum_modules (
        section_type, competency_name, competency_name_ar, order_index, icon,
        content, content_ar, description, description_ar,
        learning_objectives, learning_objectives_ar,
        estimated_duration_hours, prerequisite_module_id, quiz_id,
        quiz_required, quiz_passing_score, certification_type, is_published, created_at
    ) VALUES (
        'knowledge_based',
        'Marketing & Sales Strategies',
        'استراتيجيات التسويق والمبيعات',
        6,
        'megaphone',
        '{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"Marketing & Sales Strategies"}]},{"type":"paragraph","content":[{"type":"text","text":"Effective marketing and sales strategies are essential for business development success. This module explores integrated approaches to customer acquisition, demand generation, brand positioning, and revenue growth across traditional and digital channels."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Strategic Marketing Fundamentals"}]},{"type":"paragraph","content":[{"type":"text","text":"Marketing creates awareness, generates demand, and builds brand equity. Strategic marketing aligns promotional activities with business objectives, target customer segments, and competitive positioning to drive sustainable growth."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Segmentation, Targeting, and Positioning (STP)"}]},{"type":"paragraph","content":[{"type":"text","text":"The STP framework provides the foundation for effective marketing:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Segmentation"},{"type":"text","text":" - Dividing heterogeneous markets into homogeneous groups based on demographics, psychographics, behaviors, or needs"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Targeting"},{"type":"text","text":" - Evaluating segment attractiveness and selecting which segments to serve based on size, growth, profitability, and strategic fit"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Positioning"},{"type":"text","text":" - Crafting distinct perceptions in target customers\'' minds through differentiated value propositions and messaging"}]}]}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"The Marketing Mix (4Ps)"}]},{"type":"paragraph","content":[{"type":"text","text":"The marketing mix integrates tactical decisions across four dimensions:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Product"},{"type":"text","text":" - Features, quality, branding, packaging, and services that deliver value"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Price"},{"type":"text","text":" - Pricing strategy, discounts, payment terms balancing profitability and competitiveness"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Place"},{"type":"text","text":" - Distribution channels, market coverage, logistics ensuring availability"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Promotion"},{"type":"text","text":" - Advertising, public relations, content marketing, and personal selling driving awareness and demand"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Digital Marketing Strategies"}]},{"type":"paragraph","content":[{"type":"text","text":"Digital channels have fundamentally transformed marketing, enabling precise targeting, real-time measurement, and personalization at scale."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Content Marketing"}]},{"type":"paragraph","content":[{"type":"text","text":"Content marketing attracts and engages audiences through valuable, relevant content rather than interruptive advertising. Effective content marketing establishes thought leadership, builds trust, and drives organic traffic. Content types include blog posts, whitepapers, case studies, webinars, podcasts, videos, and infographics. Success requires consistency, quality, SEO optimization, and distribution across owned, earned, and paid channels."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Search Engine Marketing (SEM)"}]},{"type":"paragraph","content":[{"type":"text","text":"SEM combines organic search engine optimization (SEO) and paid search advertising (PPC). SEO improves rankings through technical optimization, content quality, and backlink acquisition. PPC delivers immediate visibility through targeted keyword bidding, with costs determined by competition and quality scores. Effective SEM requires keyword research, ad copy testing, landing page optimization, and continuous performance analysis."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Social Media Marketing"}]},{"type":"paragraph","content":[{"type":"text","text":"Social platforms enable direct audience engagement, community building, and viral content distribution. Platform selection depends on target demographics—LinkedIn for B2B professionals, Instagram and TikTok for younger consumers, Facebook for broad reach. Social media strategies balance organic community engagement with paid advertising for targeted reach amplification."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Email Marketing and Marketing Automation"}]},{"type":"paragraph","content":[{"type":"text","text":"Email remains one of the highest-ROI marketing channels. Marketing automation platforms enable segmentation, personalization, triggered campaigns, and lead nurturing workflows. Effective email marketing requires permission-based lists, compelling subject lines, mobile optimization, clear calls-to-action, and A/B testing for continuous improvement."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Sales Strategy and Methodologies"}]},{"type":"paragraph","content":[{"type":"text","text":"Sales converts marketing-generated demand into revenue. Modern sales approaches emphasize consultative selling, relationship building, and customer success over transactional pushing."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"The Sales Funnel"}]},{"type":"paragraph","content":[{"type":"text","text":"Sales funnels map customer journeys from awareness to purchase:"}]},{"type":"orderedList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Awareness"},{"type":"text","text":" - Target customers recognize problems or opportunities"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Interest"},{"type":"text","text":" - Customers explore potential solutions and providers"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Consideration"},{"type":"text","text":" - Customers evaluate alternatives and conduct due diligence"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Intent"},{"type":"text","text":" - Customers signal purchase readiness through requests for proposals or demos"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Purchase"},{"type":"text","text":" - Customers commit through contracts and payment"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Loyalty"},{"type":"text","text":" - Post-purchase satisfaction drives retention, expansion, and referrals"}]}]}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"SPIN Selling"}]},{"type":"paragraph","content":[{"type":"text","text":"SPIN Selling, developed by Neil Rackham, structures consultative sales conversations through four question types:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Situation"},{"type":"text","text":" - Understanding customer context and current state"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Problem"},{"type":"text","text":" - Uncovering pain points, challenges, and inefficiencies"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Implication"},{"type":"text","text":" - Exploring consequences and costs of unsolved problems"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Need-Payoff"},{"type":"text","text":" - Articulating value and benefits of proposed solutions"}]}]}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Account-Based Marketing (ABM)"}]},{"type":"paragraph","content":[{"type":"text","text":"ABM treats individual accounts as markets of one, coordinating personalized marketing and sales efforts across buying committees. Particularly effective for enterprise B2B sales, ABM requires deep account research, multi-threaded engagement, and alignment between marketing and sales teams."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Customer Relationship Management"}]},{"type":"paragraph","content":[{"type":"text","text":"CRM systems centralize customer data, track interactions, manage pipelines, and enable data-driven decision-making. Effective CRM adoption requires process standardization, data quality discipline, and integration with marketing automation platforms for seamless lead handoffs."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Marketing and Sales Alignment"}]},{"type":"paragraph","content":[{"type":"text","text":"Misalignment between marketing and sales—marketing generating unqualified leads, sales failing to follow up on opportunities—wastes resources and hampers growth. Alignment requires shared definitions of qualified leads, service level agreements on lead follow-up, regular communication, and integrated performance metrics."}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"italic"}],"text":"Mastering integrated marketing and sales strategies positions business development professionals to drive sustainable demand generation, optimize conversion rates, and accelerate revenue growth."}]}]}',
        '{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"استراتيجيات التسويق والمبيعات"}]},{"type":"paragraph","content":[{"type":"text","text":"استراتيجيات التسويق والمبيعات الفعالة ضرورية لنجاح تطوير الأعمال. تستكشف هذه الوحدة الأساليب المتكاملة لاكتساب العملاء وتوليد الطلب وتموضع العلامة التجارية."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"أساسيات التسويق الاستراتيجي"}]},{"type":"paragraph","content":[{"type":"text","text":"التسويق يخلق الوعي ويولد الطلب ويبني قيمة العلامة التجارية."}]}]}',
        'Master integrated marketing and sales strategies for demand generation, customer acquisition, brand positioning, and revenue growth across traditional and digital channels.',
        'إتقان استراتيجيات التسويق والمبيعات المتكاملة لتوليد الطلب واكتساب العملاء وتموضع العلامة التجارية ونمو الإيرادات.',
        ARRAY[
            'Apply STP framework for market segmentation, targeting, and positioning',
            'Develop comprehensive marketing mix (4Ps) strategies',
            'Execute digital marketing campaigns across content, SEM, social, and email channels',
            'Implement consultative sales methodologies including SPIN selling',
            'Align marketing and sales for optimized lead generation and conversion'
        ],
        ARRAY[
            'تطبيق إطار التجزئة والاستهداف والتموضع',
            'تطوير استراتيجيات المزيج التسويقي الشامل',
            'تنفيذ حملات التسويق الرقمي عبر المحتوى والبحث ووسائل التواصل',
            'تنفيذ منهجيات المبيعات الاستشارية',
            'مواءمة التسويق والمبيعات لتوليد العملاء المحتملين المحسّن'
        ],
        3,
        v_module_5_id,
        NULL,
        true,
        70,
        'CP',
        true,
        NOW()
    );

    -- Get Module 6 ID
    SELECT id INTO v_module_6_id
    FROM public.curriculum_modules
    WHERE order_index = 6 AND certification_type = 'CP';

    -- ----------------------------------------------------------------------------
    -- MODULE 7: Legal & Compliance in BD
    -- ----------------------------------------------------------------------------
    INSERT INTO public.curriculum_modules (
        section_type, competency_name, competency_name_ar, order_index, icon,
        content, content_ar, description, description_ar,
        learning_objectives, learning_objectives_ar,
        estimated_duration_hours, prerequisite_module_id, quiz_id,
        quiz_required, quiz_passing_score, certification_type, is_published, created_at
    ) VALUES (
        'knowledge_based',
        'Legal & Compliance in BD',
        'القانون والامتثال في تطوير الأعمال',
        7,
        'shield-check',
        '{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"Legal & Compliance in Business Development"}]},{"type":"paragraph","content":[{"type":"text","text":"Navigating legal frameworks and maintaining regulatory compliance are critical responsibilities for business development professionals. This module provides essential knowledge of contracts, intellectual property, regulatory requirements, and risk mitigation strategies across domestic and international contexts."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Contract Law Fundamentals"}]},{"type":"paragraph","content":[{"type":"text","text":"Contracts formalize business relationships, defining rights, obligations, remedies, and dispute resolution mechanisms. Effective contract management protects organizational interests while enabling collaborative partnerships."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Essential Contract Elements"}]},{"type":"paragraph","content":[{"type":"text","text":"Valid contracts require:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Offer"},{"type":"text","text":" - Clear proposal of terms by one party"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Acceptance"},{"type":"text","text":" - Unqualified agreement to offered terms"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Consideration"},{"type":"text","text":" - Exchange of value between parties"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Capacity"},{"type":"text","text":" - Legal authority of parties to enter agreements"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Legal Purpose"},{"type":"text","text":" - Lawful subject matter and objectives"}]}]}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Key Contract Types in Business Development"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Non-Disclosure Agreements (NDAs)"},{"type":"text","text":" - Protecting confidential information during negotiations"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Master Service Agreements (MSAs)"},{"type":"text","text":" - Framework agreements governing ongoing relationships"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Partnership Agreements"},{"type":"text","text":" - Terms for strategic alliances and joint ventures"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Licensing Agreements"},{"type":"text","text":" - Rights to use intellectual property"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Distribution Agreements"},{"type":"text","text":" - Terms for channel partners and resellers"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Intellectual Property Protection"}]},{"type":"paragraph","content":[{"type":"text","text":"Intellectual property (IP) represents critical strategic assets requiring proactive protection and strategic leverage."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"IP Categories"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Patents"},{"type":"text","text":" - Exclusive rights to inventions for limited periods (typically 20 years)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Trademarks"},{"type":"text","text":" - Protection for brand names, logos, and distinctive identifiers"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Copyrights"},{"type":"text","text":" - Rights in original creative works including software, content, and designs"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Trade Secrets"},{"type":"text","text":" - Confidential business information providing competitive advantage"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"Effective IP strategy requires identifying valuable IP, securing appropriate protections, monitoring infringement, and strategically licensing or defending rights."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Regulatory Compliance"}]},{"type":"paragraph","content":[{"type":"text","text":"Business development activities must comply with industry-specific regulations and cross-cutting legal requirements."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Key Regulatory Areas"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Antitrust and Competition Law"},{"type":"text","text":" - Prohibitions on anti-competitive practices including price-fixing, market allocation, and abuse of dominant position"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Data Privacy"},{"type":"text","text":" - GDPR, CCPA, and other frameworks governing personal data collection, processing, and protection"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Export Controls"},{"type":"text","text":" - Restrictions on transfer of sensitive technologies and products to certain countries"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Anti-Corruption"},{"type":"text","text":" - FCPA, UK Bribery Act prohibiting bribes and corrupt payments"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Industry-Specific Regulations"},{"type":"text","text":" - Healthcare (HIPAA), Financial Services (SOX, Dodd-Frank), etc."}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"International Business Law"}]},{"type":"paragraph","content":[{"type":"text","text":"Cross-border business development introduces additional legal complexities including jurisdictional questions, choice of law, international dispute resolution, and varying regulatory standards."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"International Contract Considerations"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Governing Law"},{"type":"text","text":" - Which jurisdiction''s laws interpret the contract"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Dispute Resolution"},{"type":"text","text":" - Arbitration, mediation, or litigation for resolving conflicts"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Currency and Payment Terms"},{"type":"text","text":" - Exchange rate risks and international payment mechanisms"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Force Majeure"},{"type":"text","text":" - Provisions addressing unforeseeable circumstances preventing performance"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Ethics and Corporate Governance"}]},{"type":"paragraph","content":[{"type":"text","text":"Beyond legal compliance, business development professionals must navigate ethical dilemmas and uphold corporate governance standards. Ethical frameworks include:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Transparency"},{"type":"text","text":" - Honest, accurate representation of capabilities and commitments"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Conflicts of Interest"},{"type":"text","text":" - Identifying and managing situations where personal interests may compromise judgment"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Fair Dealing"},{"type":"text","text":" - Treating partners, competitors, and stakeholders with integrity"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Sustainability and Social Responsibility"},{"type":"text","text":" - Considering environmental and social impacts of business activities"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Risk Management and Due Diligence"}]},{"type":"paragraph","content":[{"type":"text","text":"Systematic due diligence mitigates legal and reputational risks in partnerships and transactions. Due diligence examines:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Corporate structure, ownership, and good standing"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Financial health and stability"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Legal and regulatory compliance history"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"IP ownership and freedom to operate"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Litigation history and material disputes"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Reputation and stakeholder perceptions"}]}]}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"italic"}],"text":"Legal and compliance mastery enables business development professionals to structure deals confidently, protect organizational interests, and navigate complex regulatory landscapes across global markets."}]}]}',
        '{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"القانون والامتثال في تطوير الأعمال"}]},{"type":"paragraph","content":[{"type":"text","text":"التنقل في الأطر القانونية والحفاظ على الامتثال التنظيمي هي مسؤوليات حاسمة لمحترفي تطوير الأعمال."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"أساسيات قانون العقود"}]},{"type":"paragraph","content":[{"type":"text","text":"العقود تضفي الطابع الرسمي على العلاقات التجارية وتحدد الحقوق والالتزامات."}]}]}',
        'Gain essential knowledge of contracts, intellectual property, regulatory compliance, and risk mitigation strategies for business development across domestic and international contexts.',
        'اكتساب معرفة أساسية بالعقود والملكية الفكرية والامتثال التنظيمي واستراتيجيات تخفيف المخاطر لتطوير الأعمال.',
        ARRAY[
            'Understand contract law fundamentals and key agreement types',
            'Protect and leverage intellectual property strategically',
            'Navigate regulatory compliance including antitrust, data privacy, and anti-corruption',
            'Address international business law considerations and cross-border risks',
            'Conduct systematic due diligence for partnerships and transactions'
        ],
        ARRAY[
            'فهم أساسيات قانون العقود وأنواع الاتفاقيات الرئيسية',
            'حماية والاستفادة من الملكية الفكرية بشكل استراتيجي',
            'التنقل في الامتثال التنظيمي بما في ذلك مكافحة الاحتكار وخصوصية البيانات',
            'معالجة اعتبارات قانون الأعمال الدولية والمخاطر عبر الحدود',
            'إجراء العناية الواجبة المنهجية للشراكات والمعاملات'
        ],
        3,
        v_module_6_id,
        NULL,
        true,
        70,
        'CP',
        true,
        NOW()
    );

    -- Get Module 7 ID
    SELECT id INTO v_module_7_id
    FROM public.curriculum_modules
