    -- ============================================================================
    -- BEHAVIORAL COMPETENCIES (Modules 8-14)
    -- ============================================================================

    -- ----------------------------------------------------------------------------
    -- MODULE 8: Strategic Leadership
    -- ----------------------------------------------------------------------------
    INSERT INTO public.curriculum_modules (
        section_type, competency_name, competency_name_ar, order_index, icon,
        content, content_ar, description, description_ar,
        learning_objectives, learning_objectives_ar,
        estimated_duration_hours, prerequisite_module_id, quiz_id,
        quiz_required, quiz_passing_score, certification_type, is_published, created_at
    ) VALUES (
        'behavioral',
        'Strategic Leadership',
        'القيادة الاستراتيجية',
        8,
        'users',
        '{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"Strategic Leadership"}]},{"type":"paragraph","content":[{"type":"text","text":"Strategic leadership represents the capacity to influence others to voluntarily make decisions that enhance organizational prospects for long-term success. This module develops the mindsets, behaviors, and skills that distinguish exceptional business development leaders from mere managers."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Leadership vs. Management"}]},{"type":"paragraph","content":[{"type":"text","text":"While management focuses on planning, organizing, and controlling current operations, leadership emphasizes vision, inspiration, change, and future orientation. Effective business development professionals must master both domains:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Management"},{"type":"text","text":" - Maintaining order, achieving efficiency, executing plans, minimizing risk"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Leadership"},{"type":"text","text":" - Creating vision, inspiring change, developing people, accepting calculated risks"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Strategic Vision and Direction"}]},{"type":"paragraph","content":[{"type":"text","text":"Leaders articulate compelling visions that inspire action and provide strategic direction amid uncertainty. Effective visions are:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Aspirational"},{"type":"text","text":" - Stretching beyond current capabilities while remaining achievable"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Clear"},{"type":"text","text":" - Easily understood and communicated across organizational levels"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Memorable"},{"type":"text","text":" - Concise and evocative, creating emotional resonance"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Strategic"},{"type":"text","text":" - Aligned with market opportunities and organizational capabilities"}]}]}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Strategic Narratives"}]},{"type":"paragraph","content":[{"type":"text","text":"Beyond vision statements, leaders craft strategic narratives—compelling stories explaining where the organization is heading, why it matters, and how individuals contribute. Effective narratives connect organizational purpose to personal meaning, fostering engagement and commitment."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Leading Organizational Change"}]},{"type":"paragraph","content":[{"type":"text","text":"Business development inherently involves change—new markets, products, partnerships, or business models. Strategic leaders must navigate resistance, build momentum, and sustain change efforts."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Kotter''s 8-Step Change Model"}]},{"type":"orderedList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Create Urgency"},{"type":"text","text":" - Establish compelling reasons for change"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Build Guiding Coalition"},{"type":"text","text":" - Assemble influential stakeholders to champion change"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Form Strategic Vision"},{"type":"text","text":" - Clarify direction and strategy"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Enlist Volunteer Army"},{"type":"text","text":" - Communicate vision broadly to mobilize action"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Enable Action"},{"type":"text","text":" - Remove obstacles and empower execution"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Generate Short-Term Wins"},{"type":"text","text":" - Build momentum through visible successes"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Sustain Acceleration"},{"type":"text","text":" - Leverage credibility to drive deeper change"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Institute Change"},{"type":"text","text":" - Anchor new approaches in organizational culture"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Building High-Performing Teams"}]},{"type":"paragraph","content":[{"type":"text","text":"Business development success depends on cross-functional collaboration. Strategic leaders build teams that exceed the sum of individual capabilities."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Tuckman''s Team Development Model"}]},{"type":"paragraph","content":[{"type":"text","text":"Teams progress through predictable stages:"}]},{"type":"orderedList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Forming"},{"type":"text","text":" - Initial orientation, establishing norms and roles"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Storming"},{"type":"text","text":" - Conflict emergence as members assert positions"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Norming"},{"type":"text","text":" - Cohesion development, shared expectations"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Performing"},{"type":"text","text":" - Peak effectiveness with aligned objectives"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Adjourning"},{"type":"text","text":" - Transition as projects complete"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"Leaders must recognize stages and provide appropriate support—directive in forming, coaching during storming, facilitating in norming, delegating when performing."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Decision-Making Under Uncertainty"}]},{"type":"paragraph","content":[{"type":"text","text":"Business development involves high-stakes decisions with incomplete information. Strategic leaders employ frameworks to navigate ambiguity:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Data-Informed, Not Data-Driven"},{"type":"text","text":" - Balancing analytical rigor with intuition and experience"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Scenario Planning"},{"type":"text","text":" - Exploring multiple futures to test strategy robustness"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Reversible vs. Irreversible Decisions"},{"type":"text","text":" - Moving quickly on reversible decisions, deliberating on irreversible ones"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Bias Awareness"},{"type":"text","text":" - Recognizing cognitive biases (confirmation, anchoring, sunk cost) that distort judgment"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Developing Future Leaders"}]},{"type":"paragraph","content":[{"type":"text","text":"Strategic leaders invest in developing others'' capabilities, creating leadership pipelines. Effective development approaches include:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Mentoring"},{"type":"text","text":" - Providing guidance based on experience"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Coaching"},{"type":"text","text":" - Asking questions that develop thinking rather than providing answers"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Stretch Assignments"},{"type":"text","text":" - Delegating challenges slightly beyond current capabilities"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Feedback"},{"type":"text","text":" - Offering timely, specific, actionable input on performance"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Authentic Leadership"}]},{"type":"paragraph","content":[{"type":"text","text":"Authenticity—alignment between values and actions—builds trust and credibility. Authentic leaders demonstrate self-awareness, relational transparency, balanced processing of information, and internalized moral perspective. They acknowledge mistakes, share credit, and lead with integrity rather than manipulation."}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"italic"}],"text":"Strategic leadership mastery enables business development professionals to inspire teams, navigate change, and create lasting organizational impact beyond individual deals or initiatives."}]}]}',
        '{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"القيادة الاستراتيجية"}]},{"type":"paragraph","content":[{"type":"text","text":"القيادة الاستراتيجية تمثل القدرة على التأثير على الآخرين لاتخاذ قرارات طوعية تعزز آفاق النجاح طويل الأجل للمنظمة."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"القيادة مقابل الإدارة"}]},{"type":"paragraph","content":[{"type":"text","text":"بينما تركز الإدارة على التخطيط والتنظيم والسيطرة على العمليات الحالية، تؤكد القيادة على الرؤية والإلهام والتغيير."}]}]}',
        'Develop strategic leadership capabilities to set vision, inspire change, build high-performing teams, and make effective decisions under uncertainty.',
        'تطوير قدرات القيادة الاستراتيجية لتحديد الرؤية وإلهام التغيير وبناء فرق عالية الأداء واتخاذ قرارات فعالة في ظل عدم اليقين.',
        ARRAY[
            'Distinguish between leadership and management roles in business development',
            'Articulate compelling strategic visions and narratives',
            'Lead organizational change using proven frameworks like Kotter''s 8-Step Model',
            'Build and sustain high-performing cross-functional teams',
            'Make strategic decisions under conditions of uncertainty and ambiguity'
        ],
        ARRAY[
            'التمييز بين أدوار القيادة والإدارة في تطوير الأعمال',
            'صياغة رؤى استراتيجية مقنعة وروايات',
            'قيادة التغيير التنظيمي باستخدام أطر مثبتة',
            'بناء والحفاظ على فرق عالية الأداء متعددة الوظائف',
            'اتخاذ قرارات استراتيجية في ظروف عدم اليقين والغموض'
        ],
        3,
        v_module_7_id,
        NULL,
        true,
        70,
        'CP',
        true,
        NOW()
    );

    -- Get Module 8 ID
    SELECT id INTO v_module_8_id
    FROM public.curriculum_modules
    WHERE order_index = 8 AND certification_type = 'CP';

    -- ----------------------------------------------------------------------------
    -- MODULE 9: Effective Communication
    -- ----------------------------------------------------------------------------
    INSERT INTO public.curriculum_modules (
        section_type, competency_name, competency_name_ar, order_index, icon,
        content, content_ar, description, description_ar,
        learning_objectives, learning_objectives_ar,
        estimated_duration_hours, prerequisite_module_id, quiz_id,
        quiz_required, quiz_passing_score, certification_type, is_published, created_at
    ) VALUES (
        'behavioral',
        'Effective Communication',
        'التواصل الفعال',
        9,
        'message-circle',
        '{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"Effective Communication"}]},{"type":"paragraph","content":[{"type":"text","text":"Communication excellence is fundamental to business development success. This module develops sophisticated communication capabilities across verbal, written, and non-verbal channels, enabling you to influence stakeholders, build relationships, and drive alignment."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Communication Fundamentals"}]},{"type":"paragraph","content":[{"type":"text","text":"Effective communication transcends information transmission—it ensures shared understanding and drives action. The communication process involves:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Sender"},{"type":"text","text":" - Encoding messages with clear intent"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Message"},{"type":"text","text":" - Content, structure, and medium"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Channel"},{"type":"text","text":" - Method of transmission (verbal, written, visual)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Receiver"},{"type":"text","text":" - Decoding and interpretation"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Feedback"},{"type":"text","text":" - Confirmation of understanding"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Noise"},{"type":"text","text":" - Barriers disrupting clarity (physical, psychological, semantic)"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Active Listening"}]},{"type":"paragraph","content":[{"type":"text","text":"Listening is often more important than speaking. Active listening involves fully concentrating, understanding, responding, and remembering. Techniques include:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Attending"},{"type":"text","text":" - Maintaining eye contact, eliminating distractions, showing engagement"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Paraphrasing"},{"type":"text","text":" - Restating messages in your own words to confirm understanding"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Reflecting"},{"type":"text","text":" - Acknowledging emotions underlying words"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Clarifying"},{"type":"text","text":" - Asking questions to resolve ambiguity"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Summarizing"},{"type":"text","text":" - Synthesizing key points to ensure alignment"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Executive Communication"}]},{"type":"paragraph","content":[{"type":"text","text":"Communicating with senior executives requires conciseness, clarity, and strategic framing. Best practices include:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Start with the Bottom Line"},{"type":"text","text":" - Lead with recommendations before supporting details"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Use the Pyramid Principle"},{"type":"text","text":" - Present main idea first, then supporting arguments grouped logically"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Quantify Impact"},{"type":"text","text":" - Express benefits and costs in financial terms"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Anticipate Questions"},{"type":"text","text":" - Prepare for objections and data requests"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Respect Time"},{"type":"text","text":" - Communicate concisely, offering deeper dives only if requested"}]}]}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"The Elevator Pitch"}]},{"type":"paragraph","content":[{"type":"text","text":"Business development professionals must articulate value propositions in 30-60 seconds. Effective elevator pitches follow this structure:"}]},{"type":"orderedList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Hook"},{"type":"text","text":" - Attention-grabbing opening"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Problem"},{"type":"text","text":" - Pain point being addressed"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Solution"},{"type":"text","text":" - Your unique value proposition"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Proof"},{"type":"text","text":" - Credibility indicators"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Call to Action"},{"type":"text","text":" - Next steps"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Written Communication Excellence"}]},{"type":"paragraph","content":[{"type":"text","text":"Business writing must be clear, concise, and action-oriented. Key principles include:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Know Your Audience"},{"type":"text","text":" - Tailor tone, vocabulary, and detail level"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Use Active Voice"},{"type":"text","text":" - \"We recommend\" instead of \"It is recommended\""}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Eliminate Jargon"},{"type":"text","text":" - Use plain language unless technical terms serve clarity"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Structure Logically"},{"type":"text","text":" - Use headings, bullets, white space for scannability"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Edit Ruthlessly"},{"type":"text","text":" - Cut unnecessary words, eliminate redundancy"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Presentation Skills"}]},{"type":"paragraph","content":[{"type":"text","text":"Compelling presentations combine content mastery, visual design, and delivery excellence."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Slide Design Principles"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"One Message Per Slide"},{"type":"text","text":" - Clear slide titles stating key takeaways"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Visual Hierarchy"},{"type":"text","text":" - Size, color, position directing attention"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Data Visualization"},{"type":"text","text":" - Charts simplifying complex information"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Minimal Text"},{"type":"text","text":" - Slides support spoken narrative, not duplicate it"}]}]}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Delivery Techniques"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Vocal Variety"},{"type":"text","text":" - Modulating pace, volume, pitch for emphasis"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Body Language"},{"type":"text","text":" - Open posture, purposeful gestures, movement"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Eye Contact"},{"type":"text","text":" - Connecting with audience members individually"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Pause Power"},{"type":"text","text":" - Strategic silence emphasizing key points"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Cross-Cultural Communication"}]},{"type":"paragraph","content":[{"type":"text","text":"Global business development requires cultural intelligence. Key dimensions of cultural variation include:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"High-Context vs. Low-Context"},{"type":"text","text":" - Implicit vs. explicit communication styles"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Direct vs. Indirect"},{"type":"text","text":" - Confrontational honesty vs. diplomatic subtlety"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Individualism vs. Collectivism"},{"type":"text","text":" - Personal achievement vs. group harmony"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Power Distance"},{"type":"text","text":" - Hierarchical formality vs. egalitarianism"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Difficult Conversations"}]},{"type":"paragraph","content":[{"type":"text","text":"Business development involves delivering bad news, addressing conflict, and negotiating disagreements. The Difficult Conversations framework provides structure:"}]},{"type":"orderedList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Prepare"},{"type":"text","text":" - Clarify your purpose and desired outcome"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Open"},{"type":"text","text":" - Explain your perspective without blame"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Explore"},{"type":"text","text":" - Listen to understand their perspective"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Problem-Solve"},{"type":"text","text":" - Collaborate on mutually acceptable solutions"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Digital Communication Etiquette"}]},{"type":"paragraph","content":[{"type":"text","text":"Email, video calls, and messaging platforms require adapted communication norms:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Email"},{"type":"text","text":" - Clear subject lines, concise messages, appropriate formality"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Video Calls"},{"type":"text","text":" - Camera on, professional background, active participation"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Messaging"},{"type":"text","text":" - Respect response time expectations, use appropriate channels"}]}]}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"italic"}],"text":"Communication mastery enables business development professionals to build trust, influence decisions, and create alignment across diverse stakeholder groups."}]}]}',
        '{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"التواصل الفعال"}]},{"type":"paragraph","content":[{"type":"text","text":"التميز في التواصل أساسي لنجاح تطوير الأعمال. تطور هذه الوحدة قدرات التواصل المتطورة عبر القنوات اللفظية والمكتوبة وغير اللفظية."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"أساسيات التواصل"}]},{"type":"paragraph","content":[{"type":"text","text":"التواصل الفعال يتجاوز نقل المعلومات - فهو يضمن الفهم المشترك ويدفع العمل."}]}]}',
        'Master verbal, written, and non-verbal communication skills to influence stakeholders, build relationships, and drive organizational alignment effectively.',
        'إتقان مهارات التواصل اللفظي والمكتوب وغير اللفظي للتأثير على أصحاب المصلحة وبناء العلاقات ودفع التوافق التنظيمي بفعالية.',
        ARRAY[
            'Practice active listening techniques to understand stakeholder perspectives',
            'Communicate concisely and strategically with executive audiences',
            'Deliver compelling presentations combining content, design, and delivery excellence',
            'Navigate cross-cultural communication differences effectively',
            'Conduct difficult conversations with professionalism and empathy'
        ],
        ARRAY[
            'ممارسة تقنيات الاستماع النشط لفهم وجهات نظر أصحاب المصلحة',
            'التواصل بإيجاز واستراتيجية مع الجماهير التنفيذية',
            'تقديم عروض تقديمية مقنعة تجمع بين المحتوى والتصميم والتميز',
            'التنقل في اختلافات التواصل عبر الثقافات بفعالية',
            'إجراء محادثات صعبة بمهنية وتعاطف'
        ],
        3,
        v_module_8_id,
        NULL,
        true,
        70,
