    -- ----------------------------------------------------------------------------
    -- MODULE 10: Business Acumen
    -- ----------------------------------------------------------------------------
    INSERT INTO public.curriculum_modules (
        section_type, competency_name, competency_name_ar, order_index, icon,
        content, content_ar, description, description_ar,
        learning_objectives, learning_objectives_ar,
        estimated_duration_hours, prerequisite_module_id, quiz_id,
        quiz_required, quiz_passing_score, certification_type, is_published, created_at
    ) VALUES (
        'behavioral',
        'Business Acumen',
        'الفطنة التجارية',
        10,
        'briefcase',
        '{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"Business Acumen"}]},{"type":"paragraph","content":[{"type":"text","text":"Business acumen represents the ability to understand business situations holistically and make sound judgments that drive organizational success. This module develops the strategic thinking, commercial awareness, and systems perspective essential for business development leadership."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Understanding Business Models"}]},{"type":"paragraph","content":[{"type":"text","text":"Business acumen begins with understanding how organizations create, deliver, and capture value. Analyzing business models across industries reveals patterns, opportunities, and vulnerabilities."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Revenue Models"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Transactional"},{"type":"text","text":" - One-time product sales"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Subscription"},{"type":"text","text":" - Recurring revenue for ongoing access"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Freemium"},{"type":"text","text":" - Free basic offering, premium features for payment"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Licensing"},{"type":"text","text":" - Granting usage rights for fees or royalties"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Marketplace"},{"type":"text","text":" - Facilitating transactions between third parties"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Advertising"},{"type":"text","text":" - Monetizing attention and audience access"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Strategic Thinking"}]},{"type":"paragraph","content":[{"type":"text","text":"Strategic thinking involves seeing the bigger picture, recognizing patterns, anticipating consequences, and aligning actions with long-term objectives."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Systems Thinking"}]},{"type":"paragraph","content":[{"type":"text","text":"Business development operates within complex systems where actions create ripple effects. Systems thinking recognizes:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Interconnections"},{"type":"text","text":" - How elements influence each other"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Feedback Loops"},{"type":"text","text":" - Reinforcing or balancing dynamics"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Delays"},{"type":"text","text":" - Time lags between actions and outcomes"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Leverage Points"},{"type":"text","text":" - Where small changes create disproportionate impact"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Commercial Awareness"}]},{"type":"paragraph","content":[{"type":"text","text":"Commercial awareness means understanding market dynamics, competitive forces, customer needs, and economic trends shaping your industry."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Industry Analysis"}]},{"type":"paragraph","content":[{"type":"text","text":"Effective business development professionals continuously monitor:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Market Trends"},{"type":"text","text":" - Growth rates, emerging segments, shifting preferences"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Competitive Moves"},{"type":"text","text":" - Product launches, M&A activity, strategic pivots"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Regulatory Changes"},{"type":"text","text":" - New laws, standards, compliance requirements"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Technological Disruption"},{"type":"text","text":" - Innovations challenging existing business models"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Macroeconomic Factors"},{"type":"text","text":" - Interest rates, inflation, currency fluctuations"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Value Chain Analysis"}]},{"type":"paragraph","content":[{"type":"text","text":"Porter''s Value Chain framework examines activities creating customer value:"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Primary Activities"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Inbound Logistics - Receiving, storing, distributing inputs"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Operations - Transforming inputs into products"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Outbound Logistics - Collecting, storing, distributing outputs"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Marketing & Sales - Communicating and selling value"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Service - Maintaining and enhancing product value"}]}]}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Support Activities"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Procurement - Acquiring resources"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Technology Development - Innovation and R&D"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Human Resource Management - Recruiting, developing talent"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Firm Infrastructure - Planning, finance, legal, quality management"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"Value chain analysis identifies competitive advantages and partnership opportunities."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Resource Allocation"}]},{"type":"paragraph","content":[{"type":"text","text":"Business acumen includes judicious resource allocation—prioritizing initiatives with highest strategic impact and return potential."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Portfolio Management"}]},{"type":"paragraph","content":[{"type":"text","text":"The BCG Growth-Share Matrix categorizes business units or products:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Stars"},{"type":"text","text":" - High growth, high market share - invest for growth"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Cash Cows"},{"type":"text","text":" - Low growth, high market share - harvest profits"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Question Marks"},{"type":"text","text":" - High growth, low market share - invest selectively or divest"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Dogs"},{"type":"text","text":" - Low growth, low market share - divest or minimize investment"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Stakeholder Management"}]},{"type":"paragraph","content":[{"type":"text","text":"Business acumen recognizes that organizations serve multiple stakeholders with potentially conflicting interests:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Shareholders"},{"type":"text","text":" - Seeking financial returns"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Customers"},{"type":"text","text":" - Desiring value and service quality"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Employees"},{"type":"text","text":" - Valuing compensation, development, purpose"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Partners"},{"type":"text","text":" - Expecting mutually beneficial relationships"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Communities"},{"type":"text","text":" - Concerned with social and environmental impact"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Regulators"},{"type":"text","text":" - Enforcing legal and ethical standards"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"Effective leaders balance stakeholder interests rather than optimize for any single group."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Business Judgment"}]},{"type":"paragraph","content":[{"type":"text","text":"Business acumen culminates in sound judgment—making wise decisions despite incomplete information and competing pressures. Developing judgment requires:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Experience"},{"type":"text","text":" - Pattern recognition from diverse situations"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Reflection"},{"type":"text","text":" - Learning from successes and failures"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Diverse Perspectives"},{"type":"text","text":" - Seeking input from varied sources"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Principles"},{"type":"text","text":" - Grounding decisions in core values"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Courage"},{"type":"text","text":" - Acting decisively when clarity is unattainable"}]}]}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"italic"}],"text":"Business acumen enables professionals to navigate complexity, recognize opportunities others miss, and make decisions that create sustainable competitive advantage."}]}]}',
        '{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"الفطنة التجارية"}]},{"type":"paragraph","content":[{"type":"text","text":"الفطنة التجارية تمثل القدرة على فهم المواقف التجارية بشكل شامل واتخاذ أحكام سليمة تدفع النجاح التنظيمي."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"فهم نماذج الأعمال"}]},{"type":"paragraph","content":[{"type":"text","text":"تبدأ الفطنة التجارية بفهم كيفية إنشاء المنظمات وتقديم القيمة والاستحواذ عليها."}]}]}',
        'Develop holistic business understanding, strategic thinking, commercial awareness, and sound judgment to drive organizational success.',
        'تطوير الفهم التجاري الشامل والتفكير الاستراتيجي والوعي التجاري والحكم السليم لدفع النجاح التنظيمي.',
        ARRAY[
            'Analyze business models and revenue strategies across industries',
            'Apply systems thinking to understand organizational dynamics',
            'Maintain commercial awareness of market trends and competitive forces',
            'Conduct value chain analysis to identify competitive advantages',
            'Balance multiple stakeholder interests in business decisions'
        ],
        ARRAY[
            'تحليل نماذج الأعمال واستراتيجيات الإيرادات عبر الصناعات',
            'تطبيق التفكير النظمي لفهم الديناميكيات التنظيمية',
            'الحفاظ على الوعي التجاري لاتجاهات السوق والقوى التنافسية',
            'إجراء تحليل سلسلة القيمة لتحديد المزايا التنافسية',
            'موازنة مصالح أصحاب المصلحة المتعددين في قرارات الأعمال'
        ],
        3,
        v_module_9_id,
        NULL,
        true,
        70,
        'CP',
        true,
        NOW()
    );

    -- Get Module 10 ID
    SELECT id INTO v_module_10_id
