    WHERE order_index = 10 AND certification_type = 'CP';

    -- ----------------------------------------------------------------------------
    -- MODULE 11: Emotional Intelligence
    -- ----------------------------------------------------------------------------
    INSERT INTO public.curriculum_modules (
        section_type, competency_name, competency_name_ar, order_index, icon,
        content, content_ar, description, description_ar,
        learning_objectives, learning_objectives_ar,
        estimated_duration_hours, prerequisite_module_id, quiz_id,
        quiz_required, quiz_passing_score, certification_type, is_published, created_at
    ) VALUES (
        'behavioral',
        'Emotional Intelligence',
        'الذكاء العاطفي',
        11,
        'heart',
        '{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"Emotional Intelligence"}]},{"type":"paragraph","content":[{"type":"text","text":"Emotional Intelligence (EQ) is the capacity to recognize, understand, and manage your own emotions while also recognizing, understanding, and influencing the emotions of others. This critical competency enables business development professionals to build trust, navigate conflict, and inspire collaboration."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"The Four Domains of Emotional Intelligence"}]},{"type":"paragraph","content":[{"type":"text","text":"Daniel Goleman''s framework identifies four core EQ domains:"}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Self-Awareness"}]},{"type":"paragraph","content":[{"type":"text","text":"Understanding your emotions, strengths, weaknesses, values, and impact on others. Self-aware professionals recognize emotional triggers, acknowledge biases, and understand how their mood affects performance and relationships."}]},{"type":"paragraph","content":[{"type":"text","text":"Practices for developing self-awareness:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Reflection"},{"type":"text","text":" - Regular journaling and self-assessment"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Feedback"},{"type":"text","text":" - Seeking honest input from trusted colleagues"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Mindfulness"},{"type":"text","text":" - Present-moment awareness of thoughts and feelings"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Assessment Tools"},{"type":"text","text":" - Personality inventories like MBTI or DiSC"}]}]}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Self-Management"}]},{"type":"paragraph","content":[{"type":"text","text":"Controlling disruptive emotions and impulses, maintaining standards despite setbacks, and adapting flexibly to change. Self-management enables composure under pressure and prevents emotional reactions from undermining professional objectives."}]},{"type":"paragraph","content":[{"type":"text","text":"Key self-management competencies:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Emotional Self-Control"},{"type":"text","text":" - Managing destructive emotions and impulses"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Adaptability"},{"type":"text","text":" - Flexibility in handling change"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Achievement Orientation"},{"type":"text","text":" - Striving to meet internal standards of excellence"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Positive Outlook"},{"type":"text","text":" - Maintaining optimism despite obstacles"}]}]}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Social Awareness"}]},{"type":"paragraph","content":[{"type":"text","text":"Recognizing others'' emotions, understanding their perspectives, and sensing group dynamics. Social awareness requires empathy, organizational awareness, and service orientation."}]},{"type":"paragraph","content":[{"type":"text","text":"Developing social awareness:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Empathy"},{"type":"text","text":" - Understanding others'' emotional makeup and treating them accordingly"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Organizational Awareness"},{"type":"text","text":" - Reading organizational politics and power dynamics"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Service Orientation"},{"type":"text","text":" - Recognizing and meeting stakeholder needs"}]}]}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Relationship Management"}]},{"type":"paragraph","content":[{"type":"text","text":"Using emotional awareness to manage interactions successfully, inspire others, and navigate conflict productively. Relationship management synthesizes the other EQ domains to build influence and collaboration."}]},{"type":"paragraph","content":[{"type":"text","text":"Key competencies:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Influence"},{"type":"text","text":" - Wielding effective persuasion tactics"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Coach and Mentor"},{"type":"text","text":" - Developing others'' capabilities through feedback and guidance"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Conflict Management"},{"type":"text","text":" - Negotiating and resolving disagreements"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Teamwork"},{"type":"text","text":" - Collaborating and building team capabilities"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Empathy in Business Development"}]},{"type":"paragraph","content":[{"type":"text","text":"Empathy—understanding and sharing others'' feelings—is foundational for business development success. Three types of empathy serve different purposes:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Cognitive Empathy"},{"type":"text","text":" - Understanding others'' perspectives intellectually"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Emotional Empathy"},{"type":"text","text":" - Feeling what others feel"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Compassionate Empathy"},{"type":"text","text":" - Moved to help based on understanding"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Managing Stress and Building Resilience"}]},{"type":"paragraph","content":[{"type":"text","text":"Business development involves high pressure, uncertainty, and rejection. Emotional intelligence includes managing stress and building resilience—the capacity to recover from setbacks."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Stress Management Techniques"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Exercise"},{"type":"text","text":" - Physical activity reducing cortisol and improving mood"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Mindfulness & Meditation"},{"type":"text","text":" - Calming the mind and gaining perspective"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Social Support"},{"type":"text","text":" - Connecting with colleagues, mentors, friends, family"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Time Management"},{"type":"text","text":" - Prioritizing effectively and setting boundaries"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Reframing"},{"type":"text","text":" - Viewing challenges as growth opportunities"}]}]}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Building Resilience"}]},{"type":"paragraph","content":[{"type":"text","text":"Resilient professionals exhibit:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Growth Mindset"},{"type":"text","text":" - Believing capabilities can be developed through effort"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Purpose"},{"type":"text","text":" - Connecting work to meaningful values and impact"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Optimism"},{"type":"text","text":" - Expecting positive outcomes while preparing for challenges"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Self-Efficacy"},{"type":"text","text":" - Confidence in ability to influence outcomes"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Conflict Resolution"}]},{"type":"paragraph","content":[{"type":"text","text":"Emotional intelligence enables constructive conflict resolution. The Thomas-Kilmann Conflict Mode Instrument identifies five approaches:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Competing"},{"type":"text","text":" - Assertive and uncooperative; pursuing own concerns"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Accommodating"},{"type":"text","text":" - Unassertive and cooperative; neglecting own concerns for others"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Avoiding"},{"type":"text","text":" - Unassertive and uncooperative; not addressing conflict"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Compromising"},{"type":"text","text":" - Moderate on both dimensions; finding middle ground"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Collaborating"},{"type":"text","text":" - Assertive and cooperative; seeking win-win solutions"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"Effective conflict resolution requires selecting approaches appropriate to each situation''s importance and relationship dynamics."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Building Trust"}]},{"type":"paragraph","content":[{"type":"text","text":"Trust is the foundation of successful business relationships. The Trust Equation quantifies trust components:"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Trust = (Credibility + Reliability + Intimacy) / Self-Orientation"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Credibility"},{"type":"text","text":" - Expertise and honesty in what you say"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Reliability"},{"type":"text","text":" - Dependability in what you do"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Intimacy"},{"type":"text","text":" - Safety in sharing sensitive information"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Self-Orientation"},{"type":"text","text":" - Focus on own interests vs. others'' (inverse relationship)"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"Building trust requires demonstrating competence, keeping commitments, showing genuine care, and prioritizing others'' interests."}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"italic"}],"text":"Emotional intelligence mastery enables business development professionals to build authentic relationships, navigate organizational politics, inspire collaboration, and lead with influence and empathy."}]}]}',
        '{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"الذكاء العاطفي"}]},{"type":"paragraph","content":[{"type":"text","text":"الذكاء العاطفي هو القدرة على التعرف على عواطفك وفهمها وإدارتها مع التعرف أيضًا على مشاعر الآخرين وفهمها والتأثير عليها."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"المجالات الأربعة للذكاء العاطفي"}]},{"type":"paragraph","content":[{"type":"text","text":"يحدد إطار دانيال جولمان أربعة مجالات أساسية للذكاء العاطفي."}]}]}',
        'Develop emotional intelligence capabilities including self-awareness, self-management, social awareness, and relationship management for business development success.',
