    -- ----------------------------------------------------------------------------
    -- MODULE 12: Critical Thinking & Problem Solving
    -- ----------------------------------------------------------------------------
    INSERT INTO public.curriculum_modules (
        section_type, competency_name, competency_name_ar, order_index, icon,
        content, content_ar, description, description_ar,
        learning_objectives, learning_objectives_ar,
        estimated_duration_hours, prerequisite_module_id, quiz_id,
        quiz_required, quiz_passing_score, certification_type, is_published, created_at
    ) VALUES (
        'behavioral',
        'Critical Thinking & Problem Solving',
        'التفكير النقدي وحل المشكلات',
        12,
        'brain',
        '{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"Critical Thinking & Problem Solving"}]},{"type":"paragraph","content":[{"type":"text","text":"Critical thinking and problem-solving capabilities distinguish exceptional business development professionals from average performers. This module develops systematic approaches to analyzing complex situations, identifying root causes, generating creative solutions, and making sound decisions."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Critical Thinking Fundamentals"}]},{"type":"paragraph","content":[{"type":"text","text":"Critical thinking involves objective analysis and evaluation of issues to form judgments. It requires questioning assumptions, examining evidence, identifying logical fallacies, and considering alternative perspectives."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"The Elements of Thought"}]},{"type":"paragraph","content":[{"type":"text","text":"Richard Paul and Linda Elder''s framework identifies eight elements present in all reasoning:"}]},{"type":"orderedList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Purpose"},{"type":"text","text":" - What are we trying to accomplish?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Question"},{"type":"text","text":" - What problem are we solving?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Information"},{"type":"text","text":" - What data and evidence are we using?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Interpretation"},{"type":"text","text":" - How are we analyzing this information?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Concepts"},{"type":"text","text":" - What theories and definitions guide our thinking?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Assumptions"},{"type":"text","text":" - What are we taking for granted?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Implications"},{"type":"text","text":" - What consequences follow from our reasoning?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Point of View"},{"type":"text","text":" - What perspective are we adopting?"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Structured Problem-Solving Methodologies"}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"The 5 Whys Technique"}]},{"type":"paragraph","content":[{"type":"text","text":"Developed by Sakichi Toyoda, the 5 Whys method identifies root causes by repeatedly asking \"Why?\" until underlying issues emerge. This prevents treating symptoms while ignoring fundamental problems."}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Example:"}]},{"type":"orderedList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Problem: Partnership deal fell through"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Why? Partner lost confidence in our solution"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Why? We couldn''t demonstrate ROI convincingly"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Why? We lacked customer success metrics"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Why? We don''t systematically track customer outcomes"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Why? We haven''t implemented a customer success measurement system"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"Root Cause: Need to implement customer success tracking system."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"PDCA Cycle (Plan-Do-Check-Act)"}]},{"type":"paragraph","content":[{"type":"text","text":"Also known as the Deming Cycle, PDCA provides an iterative approach to problem-solving and continuous improvement:"}]},{"type":"orderedList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Plan"},{"type":"text","text":" - Identify problem, analyze causes, develop hypothesis"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Do"},{"type":"text","text":" - Implement solution on small scale"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Check"},{"type":"text","text":" - Measure results against expectations"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Act"},{"type":"text","text":" - Standardize if successful, or refine and repeat cycle"}]}]}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Six Thinking Hats"}]},{"type":"paragraph","content":[{"type":"text","text":"Edward de Bono''s Six Thinking Hats method structures group problem-solving by separating thinking modes:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"White Hat"},{"type":"text","text":" - Facts, data, information"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Red Hat"},{"type":"text","text":" - Emotions, feelings, intuition"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Black Hat"},{"type":"text","text":" - Caution, risks, weaknesses"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Yellow Hat"},{"type":"text","text":" - Benefits, optimism, opportunities"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Green Hat"},{"type":"text","text":" - Creativity, alternatives, new ideas"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Blue Hat"},{"type":"text","text":" - Process control, big picture, next steps"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Decision-Making Frameworks"}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Decision Matrix Analysis"}]},{"type":"paragraph","content":[{"type":"text","text":"Also known as Pugh Matrix or Grid Analysis, this approach scores alternatives against weighted criteria:"}]},{"type":"orderedList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"List options to evaluate"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Identify evaluation criteria"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Assign weights to criteria based on importance"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Score each option against each criterion"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Calculate weighted scores"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Select option with highest total"}]}]}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Cost-Benefit Analysis"}]},{"type":"paragraph","content":[{"type":"text","text":"Systematically comparing costs and benefits of alternatives to determine optimal choices. Effective CBA requires:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Identifying all relevant costs (direct, indirect, opportunity)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Quantifying all benefits (tangible and intangible)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Accounting for time value of money"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Considering probability and risk"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Comparing net present values"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Creative Problem-Solving"}]},{"type":"paragraph","content":[{"type":"text","text":"Innovation requires moving beyond conventional solutions to generate novel approaches."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Brainstorming Principles"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Defer Judgment"},{"type":"text","text":" - No criticism during idea generation"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Quantity Over Quality"},{"type":"text","text":" - Generate many ideas before evaluating"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Build on Others"},{"type":"text","text":" - Combine and improve suggested ideas"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Encourage Wild Ideas"},{"type":"text","text":" - Unconventional thinking sparks creativity"}]}]}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"SCAMPER Technique"}]},{"type":"paragraph","content":[{"type":"text","text":"SCAMPER prompts creative modifications through seven questions:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Substitute"},{"type":"text","text":" - What components can be replaced?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Combine"},{"type":"text","text":" - What elements can be merged?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Adapt"},{"type":"text","text":" - What can be adjusted to new contexts?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Modify"},{"type":"text","text":" - What attributes can be changed?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Put to Other Uses"},{"type":"text","text":" - What alternative applications exist?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Eliminate"},{"type":"text","text":" - What can be removed or simplified?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Reverse/Rearrange"},{"type":"text","text":" - What can be inverted or resequenced?"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Cognitive Biases and Mitigation"}]},{"type":"paragraph","content":[{"type":"text","text":"Awareness of cognitive biases improves decision quality:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Confirmation Bias"},{"type":"text","text":" - Seeking information confirming preexisting beliefs. Mitigation: Actively seek disconfirming evidence"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Anchoring Bias"},{"type":"text","text":" - Over-relying on first information encountered. Mitigation: Consider multiple reference points"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Sunk Cost Fallacy"},{"type":"text","text":" - Continuing investment due to past costs. Mitigation: Evaluate based on future value only"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Availability Heuristic"},{"type":"text","text":" - Overestimating likelihood of memorable events. Mitigation: Use base rate data"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Groupthink"},{"type":"text","text":" - Desire for harmony suppressing dissent. Mitigation: Encourage devil''s advocate roles"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Analytical Tools"}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"SWOT Analysis"}]},{"type":"paragraph","content":[{"type":"text","text":"Evaluating Strengths, Weaknesses, Opportunities, and Threats provides strategic situational assessment. Effective SWOT analysis pairs internal factors (S/W) with external factors (O/T) to identify strategic options."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Fishbone Diagram (Ishikawa)"}]},{"type":"paragraph","content":[{"type":"text","text":"Visual tool identifying potential causes across categories (People, Process, Equipment, Materials, Environment, Management). The fishbone structure organizes brainstormed causes leading to an effect."}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"italic"}],"text":"Critical thinking and problem-solving mastery enables business development professionals to navigate complexity, make evidence-based decisions, and develop innovative solutions to strategic challenges."}]}]}',
        '{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"التفكير النقدي وحل المشكلات"}]},{"type":"paragraph","content":[{"type":"text","text":"قدرات التفكير النقدي وحل المشكلات تميز محترفي تطوير الأعمال الاستثنائيين عن الأداء المتوسط."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"أساسيات التفكير النقدي"}]},{"type":"paragraph","content":[{"type":"text","text":"التفكير النقدي يتضمن التحليل الموضوعي وتقييم القضايا لتكوين الأحكام."}]}]}',
        'Develop critical thinking and structured problem-solving capabilities to analyze complex situations, identify root causes, and generate innovative solutions.',
        'تطوير التفكير النقدي وقدرات حل المشكلات المنظمة لتحليل المواقف المعقدة وتحديد الأسباب الجذرية وتوليد حلول مبتكرة.',
        ARRAY[
            'Apply Elements of Thought framework to analyze reasoning systematically',
