    -- ----------------------------------------------------------------------------
    -- MODULE 13: Consultative Mindset
    -- ----------------------------------------------------------------------------
    INSERT INTO public.curriculum_modules (
        section_type, competency_name, competency_name_ar, order_index, icon,
        content, content_ar, description, description_ar,
        learning_objectives, learning_objectives_ar,
        estimated_duration_hours, prerequisite_module_id, quiz_id,
        quiz_required, quiz_passing_score, certification_type, is_published, created_at
    ) VALUES (
        'behavioral',
        'Consultative Mindset',
        'العقلية الاستشارية',
        13,
        'user-check',
        '{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"Consultative Mindset"}]},{"type":"paragraph","content":[{"type":"text","text":"The consultative mindset transforms business development from transactional selling to strategic problem-solving partnership. This approach prioritizes understanding client needs, delivering value, and building long-term relationships over short-term deal-making."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"From Selling to Consulting"}]},{"type":"paragraph","content":[{"type":"text","text":"Traditional sales focuses on pushing products; consultative selling focuses on solving client problems. Key mindset shifts include:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Product-Centric → Client-Centric"},{"type":"text","text":" - Understanding client context before proposing solutions"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Telling → Asking"},{"type":"text","text":" - Asking insightful questions rather than making pitches"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Features → Outcomes"},{"type":"text","text":" - Articulating business results rather than product capabilities"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Transactions → Relationships"},{"type":"text","text":" - Building trust for ongoing partnership"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"The Consultative Selling Process"}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"1. Research and Preparation"}]},{"type":"paragraph","content":[{"type":"text","text":"Deep pre-engagement research demonstrates professionalism and enables informed conversations. Investigate:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Company strategy, recent announcements, financial performance"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Industry trends and competitive dynamics"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Key stakeholder backgrounds and priorities"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Previous engagements or touchpoints with your organization"}]}]}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"2. Discovery Through Questioning"}]},{"type":"paragraph","content":[{"type":"text","text":"Effective consultants ask questions that uncover latent needs and build shared understanding. Question types include:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Situation Questions"},{"type":"text","text":" - Understanding current state and context"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Problem Questions"},{"type":"text","text":" - Identifying pain points and challenges"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Implication Questions"},{"type":"text","text":" - Exploring consequences of unsolved problems"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Need-Payoff Questions"},{"type":"text","text":" - Quantifying value of potential solutions"}]}]}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"3. Diagnosis and Analysis"}]},{"type":"paragraph","content":[{"type":"text","text":"Synthesize information gathered to diagnose root issues. Strong consultants:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Separate symptoms from underlying causes"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Identify patterns across data points"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Frame problems from client''s perspective"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Validate hypotheses before proposing solutions"}]}]}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"4. Solution Development"}]},{"type":"paragraph","content":[{"type":"text","text":"Co-create solutions with clients rather than presenting predetermined offerings. Effective approaches:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Collaborative Design"},{"type":"text","text":" - Involving clients in solution development"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Options Presentation"},{"type":"text","text":" - Offering multiple approaches with trade-offs"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"ROI Quantification"},{"type":"text","text":" - Demonstrating financial impact"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Risk Mitigation"},{"type":"text","text":" - Addressing implementation concerns proactively"}]}]}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"5. Recommendation and Influence"}]},{"type":"paragraph","content":[{"type":"text","text":"Present recommendations using executive communication principles:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Start with Recommendation"},{"type":"text","text":" - State conclusion before supporting rationale"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Use Client Language"},{"type":"text","text":" - Frame in terms of their objectives and metrics"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Address Objections"},{"type":"text","text":" - Anticipate concerns and provide responses"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Provide Evidence"},{"type":"text","text":" - Support with data, case studies, testimonials"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Building Advisory Relationships"}]},{"type":"paragraph","content":[{"type":"text","text":"Trusted advisors earn privileged access to senior executives and strategic initiatives. Characteristics of trusted advisors:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Deep Expertise"},{"type":"text","text":" - Demonstrating subject matter mastery"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Client Focus"},{"type":"text","text":" - Prioritizing client success over personal gain"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Integrity"},{"type":"text","text":" - Being honest even when inconvenient"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Discretion"},{"type":"text","text":" - Maintaining confidentiality and professional boundaries"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Proactivity"},{"type":"text","text":" - Sharing insights without being asked"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Value-Based Conversations"}]},{"type":"paragraph","content":[{"type":"text","text":"Consultative professionals articulate value in client terms:"}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"The Value Pyramid"}]},{"type":"orderedList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Functional Value"},{"type":"text","text":" - What the solution does (features, specifications)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Economic Value"},{"type":"text","text":" - Financial impact (cost savings, revenue growth)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Experiential Value"},{"type":"text","text":" - How it feels to use (ease, satisfaction)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Symbolic Value"},{"type":"text","text":" - What it represents (status, identity, values)"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"Effective value discussions address all pyramid levels, not just functional features."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Managing Objections"}]},{"type":"paragraph","content":[{"type":"text","text":"Objections signal engagement, not rejection. The consultative approach to objections:"}]},{"type":"orderedList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Listen Fully"},{"type":"text","text":" - Don''t interrupt or dismiss"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Acknowledge"},{"type":"text","text":" - Validate the concern as legitimate"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Clarify"},{"type":"text","text":" - Ask questions to understand the real issue"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Respond"},{"type":"text","text":" - Address with evidence or alternative framing"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Confirm"},{"type":"text","text":" - Ensure the objection has been resolved"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Continuous Value Delivery"}]},{"type":"paragraph","content":[{"type":"text","text":"The consultative relationship extends beyond the initial sale. Trusted advisors:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Track Outcomes"},{"type":"text","text":" - Monitor results against agreed objectives"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Share Insights"},{"type":"text","text":" - Provide industry intelligence and best practices"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Identify New Opportunities"},{"type":"text","text":" - Proactively suggest improvements"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Facilitate Connections"},{"type":"text","text":" - Introduce valuable contacts"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Solicit Feedback"},{"type":"text","text":" - Continuously improve service delivery"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Ethical Consulting"}]},{"type":"paragraph","content":[{"type":"text","text":"The consultative mindset requires unwavering ethics:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Independence"},{"type":"text","text":" - Providing objective advice free from conflicts of interest"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Transparency"},{"type":"text","text":" - Clear about capabilities, limitations, and costs"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Client Primacy"},{"type":"text","text":" - Recommending what''s best for client, not what''s most profitable"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Professionalism"},{"type":"text","text":" - Maintaining competence through continuous learning"}]}]}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"italic"}],"text":"The consultative mindset transforms business development professionals into trusted strategic partners, driving sustainable relationships and mutual value creation."}]}]}',
        '{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"العقلية الاستشارية"}]},{"type":"paragraph","content":[{"type":"text","text":"العقلية الاستشارية تحول تطوير الأعمال من البيع التعاملي إلى شراكة حل المشكلات الاستراتيجية."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"من البيع إلى الاستشارة"}]},{"type":"paragraph","content":[{"type":"text","text":"يركز البيع التقليدي على دفع المنتجات بينما يركز البيع الاستشاري على حل مشاكل العملاء."}]}]}',
        'Adopt a consultative approach to business development, prioritizing client problem-solving, value delivery, and long-term partnership over transactional selling.',
        'اعتماد نهج استشاري لتطوير الأعمال، مع إعطاء الأولوية لحل مشاكل العملاء وتقديم القيمة والشراكة طويلة الأجل على البيع التعاملي.',
        ARRAY[
            'Transition from product-centric to client-centric business development approach',
            'Conduct discovery through insightful questioning and active listening',
            'Diagnose client problems and co-create tailored solutions collaboratively',
            'Build trusted advisor relationships through expertise, integrity, and client focus',
            'Articulate value across functional, economic, experiential, and symbolic dimensions'
        ],
        ARRAY[
            'الانتقال من نهج تطوير الأعمال المرتكز على المنتج إلى المرتكز على العميل',
            'إجراء الاكتشاف من خلال طرح أسئلة ثاقبة والاستماع النشط',
            'تشخيص مشاكل العملاء والمشاركة في إنشاء حلول مخصصة',
            'بناء علاقات مستشار موثوق من خلال الخبرة والنزاهة والتركيز على العملاء',
            'التعبير عن القيمة عبر الأبعاد الوظيفية والاقتصادية والتجريبية والرمزية'
        ],
        3,
        v_module_12_id,
        NULL,
        true,
        70,
        'CP',
        true,
        NOW()
    );

