    -- ----------------------------------------------------------------------------
    -- MODULE 14: Negotiation & Relationship Management
    -- ----------------------------------------------------------------------------
    INSERT INTO public.curriculum_modules (
        section_type, competency_name, competency_name_ar, order_index, icon,
        content, content_ar, description, description_ar,
        learning_objectives, learning_objectives_ar,
        estimated_duration_hours, prerequisite_module_id, quiz_id,
        quiz_required, quiz_passing_score, certification_type, is_published, created_at
    ) VALUES (
        'behavioral',
        'Negotiation & Relationship Management',
        'التفاوض وإدارة العلاقات',
        14,
        'handshake',
        '{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"Negotiation & Relationship Management"}]},{"type":"paragraph","content":[{"type":"text","text":"Negotiation excellence and strategic relationship management are capstone competencies for business development success. This module equips you with frameworks and techniques to create value through principled negotiation while building enduring partnerships that drive mutual success."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Negotiation Fundamentals"}]},{"type":"paragraph","content":[{"type":"text","text":"Negotiation is a process where parties with different interests seek agreement through dialogue. Effective negotiators balance assertiveness (pursuing own interests) with cooperativeness (considering others'' interests)."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Distributive vs. Integrative Negotiation"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Distributive (Win-Lose)"},{"type":"text","text":" - Fixed pie, competitive claiming of value. Appropriate for one-time transactions with no ongoing relationship"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Integrative (Win-Win)"},{"type":"text","text":" - Expanding pie, collaborative value creation. Preferred for strategic partnerships and long-term relationships"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Principled Negotiation (Harvard Method)"}]},{"type":"paragraph","content":[{"type":"text","text":"Roger Fisher and William Ury''s Getting to Yes framework provides a principled approach:"}]},{"type":"orderedList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Separate People from Problem"},{"type":"text","text":" - Be hard on issues, soft on people. Address substantive matters without damaging relationships"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Focus on Interests, Not Positions"},{"type":"text","text":" - Understand underlying needs and motivations rather than stated demands"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Generate Options for Mutual Gain"},{"type":"text","text":" - Brainstorm creative solutions before evaluating. Expand the pie before dividing it"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Insist on Objective Criteria"},{"type":"text","text":" - Base agreements on fair standards (market value, precedent, expert opinion) rather than will"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Negotiation Preparation"}]},{"type":"paragraph","content":[{"type":"text","text":"Preparation determines negotiation outcomes more than any other factor. Essential preparation elements:"}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"BATNA and Reservation Point"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"BATNA (Best Alternative To Negotiated Agreement)"},{"type":"text","text":" - Your best option if negotiation fails. Strong BATNAs provide leverage"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Reservation Point"},{"type":"text","text":" - Minimum acceptable outcome. Know when to walk away"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"ZOPA (Zone of Possible Agreement)"},{"type":"text","text":" - Range where agreement is possible, between parties'' reservation points"}]}]}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Stakeholder Analysis"}]},{"type":"paragraph","content":[{"type":"text","text":"Understand all parties involved:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Decision-makers, influencers, gatekeepers"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Interests, priorities, constraints"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Cultural and communication preferences"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Historical relationship context"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Negotiation Tactics and Strategies"}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Value Creation Tactics"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Logrolling"},{"type":"text","text":" - Trading issues of different importance to each party"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Contingent Agreements"},{"type":"text","text":" - Structuring deals based on future performance"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Post-Settlement Settlement"},{"type":"text","text":" - Improving agreements after initial deal"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Adding Issues"},{"type":"text","text":" - Expanding negotiation scope to find trade opportunities"}]}]}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Defensive Tactics"}]},{"type":"paragraph","content":[{"type":"text","text":"Recognize and respond to competitive tactics:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Anchoring"},{"type":"text","text":" - First offer influencing discussion range. Counter with objective criteria"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Good Cop/Bad Cop"},{"type":"text","text":" - One negotiator tough, another accommodating. Recognize the tactic and address it directly"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Deadline Pressure"},{"type":"text","text":" - Creating artificial urgency. Question necessity and propose extensions"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Take-It-Or-Leave-It"},{"type":"text","text":" - Final offer claims. Test seriousness and propose creative alternatives"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Cross-Cultural Negotiation"}]},{"type":"paragraph","content":[{"type":"text","text":"Global business development requires cultural intelligence in negotiation. Key cultural dimensions:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Communication Style"},{"type":"text","text":" - Direct vs. indirect, explicit vs. implicit"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Relationship vs. Task Orientation"},{"type":"text","text":" - Building trust before business vs. efficiency focus"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Time Perception"},{"type":"text","text":" - Monochronic (linear, punctual) vs. polychronic (flexible, simultaneous)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Decision-Making"},{"type":"text","text":" - Individual authority vs. consensus-building"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Strategic Relationship Management"}]},{"type":"paragraph","content":[{"type":"text","text":"Negotiation is one moment in an ongoing relationship. Strategic relationship management cultivates long-term value."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"The Relationship Life Cycle"}]},{"type":"orderedList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Awareness"},{"type":"text","text":" - Initial recognition and research"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Exploration"},{"type":"text","text":" - Testing compatibility and building rapport"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Expansion"},{"type":"text","text":" - Deepening engagement and increasing commitment"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Commitment"},{"type":"text","text":" - Strategic partnership with mutual investment"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Maintenance"},{"type":"text","text":" - Sustaining value through continuous cultivation"}]}]}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Account Planning"}]},{"type":"paragraph","content":[{"type":"text","text":"Strategic accounts require systematic planning:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Account Profiling"},{"type":"text","text":" - Deep understanding of organization, strategy, challenges"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Relationship Mapping"},{"type":"text","text":" - Identifying all stakeholders and connections"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Value Planning"},{"type":"text","text":" - Defining how to deliver unique value"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Engagement Strategy"},{"type":"text","text":" - Coordinated touchpoints across buying center"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Success Metrics"},{"type":"text","text":" - Defining objectives and tracking progress"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Influence Without Authority"}]},{"type":"paragraph","content":[{"type":"text","text":"Business development often requires influencing stakeholders without formal authority. Robert Cialdini identifies six principles of influence:"}]},{"type":"orderedList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Reciprocity"},{"type":"text","text":" - People feel obligated to return favors"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Commitment and Consistency"},{"type":"text","text":" - People align actions with prior commitments"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Social Proof"},{"type":"text","text":" - People follow what others are doing"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Authority"},{"type":"text","text":" - People defer to credible experts"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Liking"},{"type":"text","text":" - People say yes to those they like"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Scarcity"},{"type":"text","text":" - People value what''s rare or limited"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Measuring Relationship Health"}]},{"type":"paragraph","content":[{"type":"text","text":"Effective relationship management requires metrics:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Engagement Breadth"},{"type":"text","text":" - Number of stakeholders and touchpoints"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Engagement Depth"},{"type":"text","text":" - Quality and strategic importance of conversations"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Customer Satisfaction"},{"type":"text","text":" - NPS, CSAT, and qualitative feedback"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Share of Wallet"},{"type":"text","text":" - Percentage of relevant spending captured"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Advocacy"},{"type":"text","text":" - Referrals, testimonials, case study participation"}]}]}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"Repairing Damaged Relationships"}]},{"type":"paragraph","content":[{"type":"text","text":"Service failures and conflicts inevitably occur. Recovery requires:"}]},{"type":"orderedList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Acknowledge"},{"type":"text","text":" - Recognize the issue without defensiveness"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Apologize"},{"type":"text","text":" - Take responsibility sincerely"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Act"},{"type":"text","text":" - Fix the problem quickly and completely"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Amend"},{"type":"text","text":" - Provide appropriate compensation or concession"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Assure"},{"type":"text","text":" - Demonstrate changes preventing recurrence"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"Effective service recovery can strengthen relationships more than if the failure never occurred."}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"italic"}],"text":"Mastering negotiation and relationship management enables business development professionals to create enduring value, build strategic partnerships, and sustain competitive advantage through collaborative excellence."}]}]}',
        '{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"التفاوض وإدارة العلاقات"}]},{"type":"paragraph","content":[{"type":"text","text":"التميز في التفاوض والإدارة الاستراتيجية للعلاقات هي كفاءات أساسية لنجاح تطوير الأعمال."}]},{"type":"heading","attrs":{"level":2},"content":[{"type":"text","text":"أساسيات التفاوض"}]},{"type":"paragraph","content":[{"type":"text","text":"التفاوض هو عملية يسعى فيها الأطراف ذوو المصالح المختلفة إلى الاتفاق من خلال الحوار."}]}]}',
        'Master principled negotiation techniques and strategic relationship management to create value, build partnerships, and sustain competitive advantage.',
