-- ============================================================================
-- VERIFICATION QUERY
-- ============================================================================
SELECT
    certification_type,
    section_type,
    COUNT(*) as module_count,
    ARRAY_AGG(competency_name ORDER BY order_index) as modules
FROM public.curriculum_modules
WHERE certification_type = 'CP'
GROUP BY certification_type, section_type
ORDER BY section_type;
